import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as a,e as o}from"./app.6f7e30ef.js";const c="/blog/imgs/ff316ca8d4ce8c640bd1c1002c30fc13.jpg",i={},t=o('<p>\u300A\u6211\u768426\u5C81\u5973\u623F\u5BA2\u300B\uFF0C\u53C8\u540D\u300A\u5929\u7A7A\u7684\u57CE\u300B\u3002</p><p>\u8FD8\u8BB0\u5F97\u8FF7\u5F69\u548C\u671D\u9633\u4E48\u3002\u5B8C\u6574\u770B\u4E86\u4E24\u904D\u5427\uFF0C\u90D1\u94A7\u7684\u300A\u79C1\u5954\u300B\uFF0C\u897F\u5858\uFF0C\u751F\u6D3B\u7684\u65E0\u5948\u4E0E\u7F20\u7EF5\u60B1\u607B\u7684\u60C5\u7EEA\uFF0C\u4EE5\u53CA\u90A3\u4E00\u6BB5\u65F6\u95F4\u7684\u6C89\u6D78\u3002</p><h2 id="\u6458\u5F55" tabindex="-1"><a class="header-anchor" href="#\u6458\u5F55" aria-hidden="true">#</a> \u6458\u5F55</h2><p>\u90A3\u7231\u60C5\u5230\u5E95\u662F\u4EC0\u4E48\uFF0C\u53C8\u4EA7\u751F\u4F55\u5904\u5462\uFF1F\u6211\u6709\u4E9B\u60F3\u4E0D\u900F\uFF0C\u6216\u8BB8\u6240\u8C13\u7684\u7231\u60C5\u53EA\u662F\u4E00\u53EA\u5F69\u8272\u7684\u8774\u8776\uFF0C\u770B\u8D77\u6765\u7F8E\u4E3D\uFF0C\u5374\u6C38\u8FDC\u4E5F\u4E0D\u80FD\u63A5\u8FD1\uFF0C\u5018\u82E5\u4F60\u771F\u7684\u60F3\u628A\u5979\u6525\u7D27\u5728\u624B\u91CC\uFF0C\u5979\u4FBF\u4F1A\u6323\u624E\uFF0C\u7136\u540E\u5728\u6323\u624E\u4E2D\u6469\u64E6\u6389\u4E86\u6240\u6709\u7684\u8272\u5F69\uFF0C\u4ECE\u6B64\u82CD\u767D\u3002</p><p>\u201C\u751F\u547D\u82E6\u77ED\uFF0C\u6240\u4EE5\u6211\u4EEC\u6D3B\u7740\u7684\u6BCF\u4E00\u5929\u90FD\u8981\u8BA9\u81EA\u5DF1\u5FEB\u4E50\uFF01\u201D</p><p>\u201C\u662F\u554A\uFF01\u53EF\u662F\u5FEB\u4E50\u8C08\u4F55\u5BB9\u6613\u5462\uFF0C\u4EBA\u59CB\u7EC8\u662F\u4E00\u79CD\u5145\u6EE1\u6B32\u671B\u7684\u52A8\u7269\uFF0C\u53EA\u8981\u6709\u6B32\u671B\u5C31\u4E00\u5B9A\u6709\u75DB\u82E6\uFF01\u201D</p><p>\u201C\u90A3\u6B32\u671B\u53C8\u6765\u6E90\u4E8E\u4F55\u5904\u5462\uFF1F\u201D</p><p>\u201C\u4EBA\u4E4B\u672C\u6027\uFF0C\u5982\u679C\u67D0\u4E00\u5929\u4F60\u5FFD\u7136\u5BF9\u8FD9\u4E2A\u4E16\u754C\u6CA1\u4E86\u6B32\u671B\uFF0C\u4F60\u4E5F\u5C31\u4E0D\u4F1A\u7559\u604B\u4EBA\u7684\u751F\u6D3B\u4E86\uFF0C\u8FD9\u770B\u4E0A\u53BB\u662F\u4E2A\u6096\u8BBA\uFF0C\u53EF\u4E8B\u5B9E\u5C31\u662F\u5982\u6B64\uFF0C\u6211\u4EEC\u4E3A\u4E86\u6B32\u671B\u75DB\u82E6\uFF0C\u53C8\u56E0\u4E3A\u6B32\u671B\u800C\u751F\u5B58\uFF01\u201D</p><p>\u6211\u626A\u5FC3\u81EA\u95EE\uFF1A\u6211\u771F\u7684\u662F\u90A3\u4E2A\u4E00\u76F4\u9ED8\u9ED8\u5B88\u62A4\u7740\u7C73\u5F69\u7684\u56ED\u4E01\u5417\uFF1F\u5B9E\u9645\u4E0A\u6211\u5E76\u4E0D\u80FD\u786E\u5B9A\uFF0C\u81F3\u5C11\u5728\u4E0E\u7C73\u5F69\u76F8\u8BC6\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6211\u5FC3\u4E2D\u8FD8\u60E6\u8BB0\u7740\u53E6\u4E00\u6735\u82B1\uFF0C\u4F46\u53EF\u4EE5\u80AF\u5B9A\u7684\u4E00\u70B9\u662F\uFF1A\u65E0\u8BBA\u6211\u4E3A\u7C73\u5F69\u4ED8\u51FA\u591A\u5C11\uFF0C\u4E5F\u4E0D\u66FE\u60F3\u8FC7\u8981\u4ECE\u5979\u8EAB\u4E0A\u7D22\u53D6\u4E9B\u4EC0\u4E48\uFF0C\u6211\u613F\u610F\u9ED8\u9ED8\u7684\u6EE1\u8DB3\u5979\uFF0C\u5B88\u62A4\u7740\u5979\uFF0C\u770B\u7740\u5979\u5FEB\u4E50\u3002</p><p>\u7ED3\u675F\u4E86\u767D\u5929\uFF0C\u8FCE\u6765\u4E86\u665A\u4E0A\uFF0C\u6211\u548C\u7AE5\u5B50\u4F9D\u65E7\u5728\u996D\u540E\u6563\u6B65\u5728\u897F\u5858\u6CB3\u8FB9\uFF0C\u53EA\u662F\u8BA9\u6211\u7565\u611F\u9057\u61BE\u7684\u662F\uFF1A\u4ECA\u5929\u90A3\u4E2A\u7A7F\u7EA2\u8272\u5916\u5957\u7684\u6F02\u4EAE\u5973\u4EBA\u6CA1\u6709\u6765\u2026\u2026\u4E5F\u8BB8\u5979\u5DF2\u7ECF\u79BB\u5F00\u897F\u5858\u4E86\u3002</p><p>\u76F8\u8F83\u4E8E\u6211\u7684\u9057\u61BE\uFF0C\u7AE5\u5B50\u8868\u73B0\u7684\u60B2\u75DB\u6B32\u7EDD\uFF0C\u8DB4\u5728\u6211\u7684\u80A9\u8180\u4E0A\u54C0\u568E\uFF0C\u9053\uFF1A\u201C\u9633\u54E5\uFF0C\u4ECA\u5929\u662F\u6211\u7684\u707E\u96BE\u65E5\uFF0C\u5728\u8FD9\u4E00\u5929\u91CC\u6211\u5931\u53BB\u4E86\u4E24\u4E2A\u7F8E\u4E3D\u7684\u5973\u4EBA\uFF0C\u6211\u8981\u5D29\u6E83\u5566\uFF01\u201D</p><p>\u7AE5\u5B50\u7684\u60B2\u75DB\u8BA9\u6211\u60F3\u8D77\u4E86\u5C0F\u65F6\u5019\u7684\u81EA\u5DF1\uFF0C\u90A3\u65F6\u5019\u6211\u4E5F\u559C\u6B22\u4E24\u4E2A\u5973\u4EBA\uFF0C\u4E00\u4E2A\u662F\u51B7\u82E5\u51B0\u971C\u7684\u5C0F\u9F99\u5973\uFF0C\u4E00\u4E2A\u662F\u6E29\u67D4\u8D24\u6DD1\u7684\u767D\u7D20\u8D1E\uFF0C\u6211\u65F6\u5E38\u7EA0\u7ED3\u957F\u5927\u4EE5\u540E\u8981\u5A36\u8C01\uFF0C\u597D\u4F3C\u5A36\u4E86\u8C01\uFF0C\u653E\u5F03\u8C01\uFF0C\u90FD\u4F1A\u96BE\u8FC7\uFF0C\u53EF\u73B0\u5728\u60F3\u6765\uFF0C\u5979\u4EEC\u4E0E\u6211\u90FD\u6CA1\u4EC0\u4E48\u5173\u7CFB\uFF0C\u5C0F\u65F6\u5019\u7684\u7EA0\u7ED3\u6839\u672C\u6CA1\u6709\u9053\u7406\u3002</p><p>\u6211\u60F3\uFF0C\u7AE5\u5B50\u4E5F\u66FE\u5728\u5FC3\u91CC\u7EA0\u7ED3\u8FC7\uFF0C\u4ED6\u5230\u5E95\u662F\u8981\u4E50\u7476\u8FD8\u662F\u8981\u90A3\u4E2A\u7EA2\u8272\u5916\u5957\u7684\u5973\u4EBA\uFF0C\u6240\u4EE5\u4ED6\u624D\u4F1A\u8BF4\u51FA\uFF1A\u8FD9\u4E00\u5929\u4ED6\u5931\u53BB\u4E86\u4E24\u4E2A\u7F8E\u4E3D\u7684\u5973\u4EBA\uFF0C\u8FD9\u79CD\u8BDD\u3002</p><p>\u53EF\u6211\u4E0D\u4F1A\u56E0\u6B64\u5632\u7B11\u4ED6\uFF0C\u56E0\u4E3A\u8FD9\u79CD\u4EE5\u5904\u7537\u4E4B\u8EAB\u800C\u5C55\u5F00\u7684\u4E0D\u8BA1\u540E\u679C\u7684\u5E7B\u60F3\u6070\u6070\u662F\u6700\u5E78\u798F\u7684\uFF0C\u53EF\u6211\u5374\u65E9\u5DF2\u7ECF\u5728\u9886\u609F\u4E4B\u540E\u5931\u53BB\u4E86\u5E7B\u60F3\u7684\u539F\u59CB\u52A8\u529B\uFF0C\u6240\u4EE5\u624D\u6D3B\u5F97\u8FD9\u4E48\u67AF\u71E5\uFF01</p><p>\u4E50\u7476\u53C8\u80AF\u5B9A\u7684\u70B9\u4E86\u70B9\u5934\uFF0C\u9053\uFF1A\u201C\u662F\u7684\u2026\u2026\u524D\u4E9B\u65E5\u5B50\u6211\u4E00\u76F4\u5728\u4E00\u4E2A\u504F\u8FDC\u7684\u5C71\u533A\u62CD\u620F\uFF0C\u90A3\u91CC\u751A\u81F3\u8FDE\u7535\u90FD\u6CA1\u6709\u901A\u4E0A\uFF0C\u5C71\u6C11\u4EEC\u7684\u5168\u90E8\u751F\u6D3B\u4FBF\u662F\u90A3\u4E00\u4EA9\u51E0\u5206\u5730\uFF0C\u95F2\u65F6\u7684\u6D3B\u52A8\u5C31\u662F\u5750\u5728\u7530\u57C2\u4E0A\u5520\u5520\u55D1\uFF0C\u53EF\u662F\u6211\u89C9\u5F97\u4ED6\u4EEC\u5F88\u5E78\u798F\uFF0C\u4E5F\u60F3\u660E\u767D\u4E86\u4E9B\u9053\u7406\uFF0C\u5176\u5B9E\u4EBA\u7684\u4E00\u5207\u75DB\u82E6\u53EA\u662F\u6E90\u4E8E\u628A\u81EA\u5DF1\u770B\u5F97\u8FC7\u4E8E\u91CD\u8981\uFF0C\u592A\u60F3\u6EE1\u8DB3\u81EA\u5DF1\u5185\u5FC3\u90A3\u4E9B\u865A\u65E0\u7F25\u7F08\u7684\u6B32\u671B\uFF0C\u6BD4\u5982\u7231\u60C5\uFF0C\u4E00\u65E6\u4E0D\u662F\u81EA\u5DF1\u6240\u5E7B\u60F3\u51FA\u6765\u7684\u6837\u5B50\u4FBF\u4F1A\u75DB\u82E6\u2026\u2026\u5B9E\u9645\u4E0A\u8BF4\u900F\u4E86\uFF0C\u7231\u60C5\u4E0D\u8FC7\u662F\u6E29\u9971\u8FC7\u540E\u7684\u4E00\u79CD\u6D88\u9063\uFF0C\u4E0D\u5FC5\u592A\u8BA4\u771F\uFF01\u201D</p><p>\u591C\u53C8\u6DF1\u4E86\u4E00\u4E9B\uFF0C\u6211\u70B9\u4E0A\u4E00\u652F\u70DF\u89E3\u5F00\u4E86\u5FC3\u4E2D\u7684\u5B64\u5BC2\uFF0C\u5168\u8EAB\u5FC3\u6295\u5165\u5230\u4E86\u63A5\u4E0B\u6765\u8981\u6539\u9020\u5BA2\u6808\u7684\u5DE5\u4F5C\u4E2D\u3002</p><p>\u6211\u6D4F\u89C8\u7740\u5404\u4E2A\u65C5\u6E38\u7F51\u7AD9\uFF0C\u6536\u96C6\u7740\u6E38\u5BA2\u4EEC\u5728\u897F\u5858\u65C5\u6E38\u540E\u5199\u4E0B\u7684\u611F\u609F\u3002</p><p>\u6211\u8981\u501F\u52A9\u8FD9\u4E9B\u611F\u609F\u5F04\u6E05\u695A\u4ED6\u4EEC\u7684\u9700\u6C42\uFF0C\u5728\u5546\u4E1A\u8425\u9500\u4E0A\uFF0C\u6211\u6DF1\u4FE1\uFF1A\u4E86\u89E3\u5E76\u6EE1\u8DB3\u5BA2\u6237\u7684\u9700\u6C42\uFF0C\u624D\u662F\u83B7\u5F97\u5546\u4E1A\u5229\u76CA\u7684\u57FA\u7840\u3002</p><p>\u89E3\u9152\u8336</p><p>\u6211\u7684\u8111\u6D77\u4E2D\u968F\u5373\u6D6E\u73B0\u51FA\u4E00\u5E45\u753B\u9762\uFF1A\u6211\u4EEC\u5728\u4E00\u4E2A\u7A7A\u95F2\u7684\u5468\u672B\uFF0C\u53BB\u90A3\u4E2A\u8FDC\u79BB\u5DE5\u4E1A\u673A\u68B0\u7684\u5E7F\u573A\uFF0C\u73A9\u7740\u8D5B\u8F66\uFF0C\u7D2F\u4E86\uFF0C\u5979\u4FBF\u9760\u5728\u6211\u7684\u80A9\u8180\u4E0A\u4F11\u606F\uFF0C\u76F4\u5230\u9EC4\u660F\u65F6\u6211\u4EEC\u624D\u79BB\u5F00\uFF0C\u7136\u540E\u63D0\u7740\u4ECE\u83DC\u5E02\u573A\u4E70\u6765\u7684\u83DC\uFF0C\u56DE\u5230\u5C0F\u533A\u95E8\u53E3\u7684\u4FBF\u5229\u5E97\uFF0C\u5750\u5728\u6728\u9A6C\u4E0A\u6643\u8361\u4E00\u4F1A\u513F\uFF0C\u6700\u540E\u56DE\u5230\u8001\u5C4B\u5B50\uFF0C\u4E00\u8D77\u505A\u4E00\u987F\u4E0D\u7B97\u4E30\u76DB\u5374\u5F88\u6E29\u99A8\u7684\u665A\u9910.......</p><p>\u8FD9\u770B\u4E0A\u53BB\u662F\u4E00\u79CD\u7B80\u5355\u7684\u4E0D\u80FD\u518D\u7B80\u5355\u7684\u751F\u6D3B\uFF0C\u5374\u662F\u6211\u68A6\u5BD0\u4EE5\u6C42\u7684\uFF0C\u4E8E\u662F\u6C89\u6D78\u5728\u8FD9\u4E2A\u865A\u8BBE\u7684\u573A\u666F\u4E2D\u6709\u4E9B\u604D\u60DA\uFF0C\u8BB8\u4E45\u624D\u7ED9\u7C73\u5F69\u56DE\u4E86\u4FE1\u606F\uFF1A\u201C\u7B49\u4F60\u56DE\u6765\uFF0C\u6211\u4EEC\u4FBF\u8FC7\u8FD9\u79CD\u751F\u6D3B\u3002\u201D</p><p>\u63D0\u62C9\u7C73\u82CF</p><p>\u4E00\u7F16\u7EC7\u888B\u7684\u5564\u9152\uFF0C\u5C31\u8FD9\u4E48\u88AB\u6211\u548C\u7F57\u672C\u559D\u5B8C\uFF0C\u800C\u65F6\u95F4\u4E5F\u4ECE\u9EC4\u660F\u6765\u5230\u4E86\u591C\u665A\uFF0C\u62A4\u57CE\u6CB3\u8FB9\u7684\u8DEF\u706F\u4E0D\u77E5\u9053\u4EC0\u4E48\u65F6\u5019\u4EAE\u4E86\u8D77\u6765\uFF0C\u548C\u4E0A\u6B21\u4E00\u822C\uFF0C\u706F\u6746\u5728\u6CB3\u9762\u843D\u4E0B\u4E00\u4E2A\u4E2A\u5E73\u884C\u7684\u5F71\u5B50\uFF0C\u8FD8\u6709\u4E00\u7247\u5FAE\u5F31\u7684\u95EA\u4EAE\u3002</p><p>\u6211\u5C06\u90A3\u8F86\u5927\u5207\u8BFA\u57FA\u9057\u5F03\u5728\u505C\u8F66\u573A\u5185,\u4E00\u4E2A\u4EBA\u8D70\u5728\u4E86\u4EBA\u7FA4\u6D8C\u52A8\u7684\u8857\u5934,\u6211\u75DB\u4E86,\u8FD9\u6B21\u771F\u7684\u75DB\u4E86,\u75DB\u5230\u8BB0\u4E0D\u5F97\u53BB\u501F\u9152\u6D88\u6101,\u75DB\u5230\u8BB0\u4E0D\u5F97\u8981\u6D41\u4E0A\u4E9B\u773C\u6CEA\u53BB\u796D\u5960\u8FD9\u6BB5\u5DF2\u7ECF\u6D88\u901D\u7684\u7231\u60C5......\u4E8E\u662F,\u6211\u770B\u4E0A\u53BB\u662F\u90A3\u4E48\u7684\u5E73\u9759,\u5E73\u9759\u5230\u6DF9\u6C92\u5728\u4EBA\u7FA4\u91CC,\u6C92\u6709\u4EBA\u6CE8\u610F\u5230\u6211\u7684\u5B58\u5728,</p><p>\u751F\u6D3B,\u592A\u65E0\u5E38\u4E86,\u4EBA\u4E5F\u592A\u65E0\u5E38\u4E86......\u800C\u6211\u5374\u56FA\u6267\u7684\u53BB\u8FFD\u6C42\u4E00\u4EFD\u6C38\u8FDC\u4E0D\u4F1A\u8FC7\u671F\u7684\u7231\u60C5,\u6B8A\u4E0D\u77E5\u8FDE\u90A3\u4FDD\u9C9C\u819C\u90FD\u4F1A\u8FC7\u671F,\u4F55\u51B5\u7231\u60C5\u5462,\u53EF\u5373\u4FBF\u770B\u900F\u4E86\u8FD9\u4E9B,\u90A3\u4E9B\u4E0D\u53EF\u963B\u6321\u7684\u75DB\u82E6,\u4F9D\u65E7\u662F\u90A3\u4E48\u7684\u771F\u5B9E,\u4E8E\u662F\u6211\u76F8\u4FE1\uFF1A\u8FD9\u4E2A\u4E16\u754C\u4E0A\u552F\u4E00\u4E0D\u4F1A\u8FC7\u671F\u7684\u4FBF\u662F\u90A3\u4E9B\u907F\u4E4B\u4E0D\u53CA\u7684\u75DB\u82E6......\u6211\u597D\u4F3C\u53C8\u5C06\u5012\u5728\u8FD9\u4E9B\u75DB\u82E6\u4E2D\u5815\u843D\u3001\u6C89\u6CA6,</p><p>\u8DEF\u5728\u6211\u7684\u773C\u524D\u6C92\u6709\u5C3D\u5934\u7684\u5EF6\u4F38\u7740,\u6211\u62D6\u7740\u4E00\u53CC\u75B2\u5026\u7684\u817F,\u8D70\u5728\u7269\u662F\u4EBA\u975E\u7684\u666F\u8272\u91CC,\u597D\u4F3C\u770B\u5230\u4E86\u4E00\u79CD\u6DF1\u523B\u7684\u9519,\u4E3A\u4EC0\u4E48\u5982\u6B64\u4E00\u4E2A\u7B80\u5355\u7684\u591C\u665A,\u6211\u5374\u6495\u5FC3\u88C2\u80BA\u7684\u548C\u5979\u5206\u624B\u4E86,\u800C\u66FE\u7ECF\u4E3A\u5F7C\u6B64\u505A\u8FC7\u7684\u4E00\u5207,\u6700\u7EC8\u4E5F\u53EA\u662F\u5316\u6210\u4E00\u4E9B\u6563\u843D\u7684\u7247\u6BB5,\u5728\u8BB0\u5FC6\u91CC\u8D70\u4E0A\u4E00\u904D\u540E,\u4FBF\u9762\u4E34\u7740\u88AB\u6C38\u8FDC\u9057\u5FD8\u7684\u547D\u8FD0......</p><p>\u6211\u662F\u90A3\u4E48\u7684\u820D\u4E0D\u5F97,\u53EF\u5374\u771F\u771F\u5207\u5207\u7684\u5206\u624B\u4E86,\u4E8E\u662F\u7528\u5206\u624B\u540E\u5F97\u4F86\u7684\u81EA\u7531\u5B89\u6170\u7740\u81EA\u5DF1......\u53EF\u5E76\u4E0D\u77E5\u9053,\u5269\u4F59\u7684\u4EBA\u751F\u4E2D,\u81EA\u5DF1\u8FD8\u4F1A\u7231\u4E0A\u8C01,......\u60F3\u4F86,\u662F\u4E0D\u613F\u610F\u518D\u53BB\u523B\u9AA8\u94ED\u5FC3\u7684\u7231\u4E86,\u4EE5\u540E\u627E\u4E2A\u5E73\u51E1\u7684\u5973\u4EBA,\u5E73\u51E1\u7684\u8FC7\u5B8C\u4E00\u751F\u4FBF\u597D,</p><p>\u6768\u4ECE\u5BB9\u7EC8\u4E8E\u5BF9\u6211\u8BF4\u9053\uFF1A\u201C\u6211\u662F\u4E2A\u5546\u4EBA\uFF0C\u5F88\u591A\u4E8B\u60C5\u6211\u4F1A\u505A\u51FA\u5224\u65AD\u548C\u8861\u91CF\uFF0C\u8FD9\u4E2A\u4E16\u754C\u4E0A\u6700\u4E0D\u7F3A\u7684\u5C31\u662F\u53EF\u4EE5\u8D5A\u94B1\u7684\u60F3\u6CD5\uFF0C\u4F46\u662F\u628A\u8FD9\u4E9B\u60F3\u6CD5\u53D8\u6210\u771F\u91D1\u767D\u94F6\u5E02\u662F\u9700\u8981\u673A\u9047\u548C\u9020\u5316\u7684\uFF0C\u8FD9\u5C31\u662F\u6211\u6295\u8D44\u8DEF\u9177\u7684\u539F\u56E0\u3002\u201D</p><p>\u518D\u770B\u770B\u6211\u81EA\u5DF1,\u6211\u604D\u7136\u4E0D\u77E5\u9053\u8FD9\u4E48\u591A\u5E74\u90FD\u5E72\u4E86\u4E9B\u4EC0\u4E48,\u66FE\u7ECF\u6211\u4E5F\u548C\u7F57\u672C\u4E00\u8D77\u73A9\u8FC7\u4E50\u961F,\u5199\u8FC7\u6B4C,\u4E5F\u6D88\u6781\u7684\u5728\u804C\u573A\u6DF7\u8FC7,\u53EF\u6700\u540E\u4F9D\u7136\u4E00\u4E8B\u65E0\u6210,\u800C\u4EBA\u6700\u6015\u7684\u5C31\u662F\u5BF9\u6BD4,\u73B0\u5728\u7684\u6211\u5DF2\u7ECF\u88AB\u7F57\u672C\u3001\u5411\u6668\u3001\u751A\u81F3\u65B9\u5706\u7529\u5728\u4E86\u5F88\u8FDC\u7684\u540E\u65B9,\u66F4\u4E3A\u7CDF\u7CD5\u7684\u662F\uFF1A\u6211\u6682\u65F6\u6839\u672C\u6C92\u6709\u4E00\u4E2A\u660E\u786E\u7684\u4EBA\u751F\u65B9\u5411......\u8FDE\u8DEF\u9177\u8FD9\u4E2A\u516C\u53F8\u548C\u662D\u603B\u7684\u540D\u5934,\u90FD\u8BA9\u6211\u5728\u60F3\u8D77\u4F86\u65F6,\u5145\u6EE1\u4E86\u60F6\u6050,\u73B0\u5728\u7684\u6211,\u5C31\u50CF\u4E00\u4E2A\u6C92\u6709\u4EBA\u80FD\u5BF9\u8BDD\u7684\u5B69\u7AE5,\u751F\u547D\u91CC\u5C3D\u662F\u5F77\u5FA8\u548C\u5931\u63AA,</p><p>\u4EBA\u6027\u7684\u80CC\u540E\u662F\u767D\u4E91\u82CD\u72D7</p><p>\u8FD9\u4E2A\u4E8B\u4EF6\u7ED9\u6211\u6572\u54CD\u4E86\u8B66\u949F\uFF0C\u4E00\u76F4\u4EE5\u6765\uFF0C\u6211\u4EEC\u6240\u8BBE\u60F3\u7684\u4EC5\u4EC5\u662F\u56DE\u53BB\u540E\u7684\u7B80\u5355\u548C\u8F7B\u677E\uFF0C\u4EE5\u4E3A\u4E24\u4E2A\u4EBA\u5728\u4E00\u8D77\u5C31\u53EF\u4EE5\u628A\u751F\u6D3B\u8FC7\u6210\u7AE5\u8BDD\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u5584\u4E8E\u5236\u9020\u77DB\u76FE\u7684\u751F\u6D3B\uFF0C\u771F\u7684\u4E0D\u4F1A\u5982\u60F3\u8C61\u4E2D\u8FD9\u4E48\u7B80\u5355\uFF0C\u800C\u5C0F\u4E8B\u4EF6\u770B\u4E0A\u53BB\u90FD\u6CA1\u6709\u6740\u4F24\u529B\uFF0C\u4F46\u662F\u7D2F\u79EF\u8D77\u6765\u4E4B\u540E\uFF0C\u5374\u662F\u53EF\u4EE5\u4EA7\u751F\u6BC1\u706D\u529B\u91CF\u7684......\u8FD9\u5728\u751F\u6D3B\u4E2D\u5DF2\u7ECF\u6709\u592A\u591A\u7684\u4F8B\u5B50\uFF01</p><p>\u6211\u611F\u5230\u60CA\u8BB6\uFF0C\u95EE\u9053\uFF1A\u201C\u8FD9\u4E9B\u5979\u90FD\u544A\u8BC9\u4F60\u4E86\uFF1F\u201D</p><p>\u201C\u6211\u8BF4\u4E86\u9633\u54E5\u4F60\u662F\u6211\u6700\u597D\u7684\u670B\u53CB\uFF0C\u5979\u5BF9\u6211\u5F53\u7136\u6C92\u6709\u6212\u5907\u5FC3\u7406\uFF0C\u8FD9\u6C9F\u901A\u8D77\u4F86\u5C31\u5F88\u7545\u901A\u4E86\uFF0C\u800C\u4E14\u6211\u4E5F\u89C9\u5F97\u81EA\u5DF1\u662F\u4E2A\u5F88\u4E0D\u9519\u7684\u804A\u5929\u5BF9\u8C61\uFF0C\u5979\u5185\u5FC3\u7684\u4F24\u611F\uFF0C\u548C\u4E0D\u559C\u6B22\u7684\u8BDD\u984C\uFF0C\u6211\u575A\u51B3\u4E0D\u548C\u5979\u8BF4\u3002\u201D</p><p>\u201C\u4F60\u8FD8\u771F\u6709\u5957\u8DEF\u2026\u201D\u6211\u4E0D\u7981\u611F\u53F9\u3002</p><p>\u201C\u8FD9\u4E0D\u662F\u5957\u8DEF\uFF0C\u662F\u56E0\u4E3A\u6211\u5728\u610F\u5979\uFF0C\u6240\u4EE5\u53EA\u60F3\u5E26\u7ED9\u5979\u5FEB\u4E50\u7684\u60C5\u7EEA......\u9633\u54E5\uFF0C\u770B\u6837\u5B50\u4F60\u751F\u5E73\u5E76\u6C92\u6709\u82B1\u8FC7\u5FC3\u601D\u53BB\u8FFD\u8FC7\u4E00\u4E2A\u5973\u4EBA\u5427\uFF0C\u4ECE\u4F60\u5E73\u5E38\u7684\u8A00\u884C\u4E3E\u6B62\u5C31\u80FD\u770B\u51FA\u4F86......\u6709\u65F6\u5019\u771F\u7684\u53D1\u73B0\u4F60\u5E76\u4E0D\u662F\u5F88\u61C2\u5973\u4EBA\u2026\u201D</p><p>\u7C73\u5F69\u7A0D\u7A0D\u60F3\u4E86\u60F3\uFF0C\u8BF4\u9053\uFF1A\u201C\u66FE\u7ECF\u4F60\u548C\u7B80\u8587\u4E4B\u95F4\u7684\u8FD9\u79CD\u5DEE\u8DDD\u4E5F\u5F88\u660E\u663E......\u53EF\u662F\u51E0\u5E74\u540E\u5462\uFF1F\u5979\u62E5\u6709\u7684\u4E00\u5207\u90FD\u5DF2\u7ECF\u6210\u4E3A\u4E86\u6CE1\u6CAB......\u662D\u9633\uFF0C\u4F60\u5F97\u4E86\u89E3\uFF0C\u8FD9\u5C31\u662F\u4E00\u4E2A\u6B64\u6D88\u5F7C\u957F\u7684\u4E16\u754C\uFF0C\u6240\u8C13\u5DEE\u8DDD\u53EF\u4EE5\u5B58\u5728\u4E8E\u660E\u9762\u4E0A\uFF0C\u4F46\u662F\u7EDD\u5BF9\u4E0D\u80FD\u5B58\u5728\u4E8E\u6211\u4EEC\u7684\u5185\u5FC3\u2026......\u56E0\u4E3A\u8C01\u90FD\u4E0D\u4E86\u89E3\u660E\u5929\u4F1A\u53D1\u751F\u4E9B\u4EC0\u4E48\u2026......\u800C\u5728\u8FD9\u4E2A\u8FDE\u751F\u547D\u90FD\u5F88\u8106\u5F31\u7684\u4E16\u754C\u91CC\uFF0C\u8C01\u53C8\u6562\u575A\u5B9A\u7684\u8BF4\u4E00\u53E5\uFF0C\u4E0D\u4F1A\u4E22\u6389\u81EA\u5DF1\u6240\u62E5\u6709\u7684\u4E00\u5207\uFF1F......\u6240\u4EE5\uFF0C\u5E73\u5E38\u5FC3\u5C31\u597D\u2026\u201D</p><p>\u5A5A\u793C\u7684\u73AF\u8282\u5C31\u6B64\u5F00\u59CB\uFF0C\u706F\u5149\u53C8\u4E00\u6B21\u95EA\u8000\u5A5A\u793C\u7684\u821E\u53F0\uFF0C\u53F8\u4EEA\u5F88\u6709\u7ECF\u9A8C\u7684\u628A\u63A7\u7740\u8282\u594F\u548C\u6C14\u6C1B\uFF0C\u8BA9\u5A5A\u793C\u73B0\u573A\u4E00\u76F4\u5145\u6EE1\u7740\u6E29\u60C5\uFF0C\u4E8B\u5B9E\u4E0A\uFF0C\u6309\u7167\u4E8B\u5148\u7684\u60F3\u6CD5\uFF0C\u6E29\u60C5\u4FBF\u662F\u8FD9\u573A\u5A5A\u793C\u7684\u57FA\u8C03\uFF0C\u56E0\u4E3A\u7C73\u5F69\u662F\u4E2A\u5F88\u6DE1\u6F20\u7684\u4EBA\uFF0C\u8FC7\u4E8E\u717D\u52A8\u548C\u6D3B\u8DC3\u7684\u6C14\u6C1B\uFF0C\u5E76\u4E0D\u662F\u5979\u559C\u6B22\u7684\uFF0C\u800C\u4E0E\u5979\u76F8\u5904\u4E86\u8FD9\u4E48\u4E45\uFF0C\u6211\u7684\u9700\u6C42\u660E\u663E\u53D7\u5230\u5979\u7684\u5F71\u54CD\uFF0C\u4E5F\u5F88\u559C\u6B22\u8FD9\u6E29\u60C5\u7684\u5A5A\u793C\u6C14\u6C1B\uFF0C\u4E8E\u662F\uFF0C\u6211\u4EEC\u90FD\u5728\u5E73\u548C\u7684\u56DE\u5473\u7740\u4ECE\u76F8\u8BC6\u5230\u73B0\u5728\u6240\u53D1\u751F\u7684\u4E00\u5E55\u53C8\u4E00\u5E55\u2026</p><h2 id="\u7248\u672C" tabindex="-1"><a class="header-anchor" href="#\u7248\u672C" aria-hidden="true">#</a> \u7248\u672C</h2><ul><li>\u539F\u8C05\u6211\u8BFB\u7684\u662F\u7F51\u4E0A\u4E0B\u8F7D\u7684TXT\u7248\u672C\uFF0C\u7ED3\u675F\u4E8E 2018.10.28</li><li>\u6211\u768426\u5C81\u5973\u623F\u5BA2\uFF08\u5929\u7A7A\u7684\u57CE\uFF09 / \u8D85\u7EA7\u5927\u5766\u514B\u79D1\u6BD4</li><li><img src="'+c+'" alt="" loading="lazy"></li></ul>',39),r=[t];function n(l,s){return e(),a("div",null,r)}const h=p(i,[["render",n],["__file","2018-10-28-book-wo-de-26-sui-nv-fang-ke.html.vue"]]);export{h as default};
