import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as o,e as r}from"./app.6f7e30ef.js";const s={},n=r("<p>\u6700\u8FD1\u5728\u77E5\u4E4E\u4E0A\u770B\u5230\u4E86\u4E00\u4E9B\u9E21\u6C64\uFF0C\u89C9\u5F97\u5F88\u6709\u9053\u7406\uFF0C\u5F88\u7B26\u5408\u5F53\u524D\u7684\u4E3B\u6D41\u4EF7\u503C\u89C2\uFF0C\u6240\u4EE5\u6458\u6284\u4E0B\u6765\uFF08\u867D\u7136\u5E76\u65E0\u5375\u7528\uFF09\u3002</p><p><strong>1\u3001\u4E0D\u8981\u628A\u52AA\u529B\u7684\u8FC7\u7A0B\u5F53\u505A\u4F60\u70AB\u8000\u7684\u8D44\u672C\uFF0C\u5982\u679C\u4F60\u771F\u7684\u8981\u70AB\u8000\uFF0C\u8BF7\u76F4\u63A5\u4E0A\u6210\u679C\u3002</strong></p><p>\u6211\u4E0D\u77E5\u9053\u6709\u591A\u5C11\u6B21\u5728\u670B\u53CB\u5708\u91CC\u770B\u5230\u201C\u6668\u7EC3\u6253\u5361\u7B2C\u4E00\u5929\u201D\u7684\u7167\u7247\uFF0C\u4F46\u5947\u602A\u7684\u662F\uFF0C\u6211\u670B\u53CB\u5708\u91CC\u5230\u73B0\u5728\u4E5F\u6CA1\u6709\u51FA\u73B0\u8FC71\u4E2A\u8FDE\u7EED\u6253\u5361\u8D85\u8FC710\u5929\u7684\u9009\u624B\u3002</p><p>\u6211\u4E0D\u77E5\u9053\u6709\u591A\u5C11\u6B21\u5728\u670B\u53CB\u5708\u91CC\u770B\u5230\u5728\u5E72\u51C0\u6574\u6D01\u7684\u684C\u9762\u4E0A\u5F00\u7740\u7F8E\u989C\u770B\u4E66\u7684\u6587\u827A\u7167\u7247\uFF0C\u4F46\u5947\u602A\u7684\u662F\uFF0C\u6211\u670B\u53CB\u5708\u91CC\u5F88\u5C11\u6709\u4EBA\u770B\u5B8C\u4E00\u672C\u4E66\u4EE5\u540E\u53D1\u8868\u4EC0\u4E48\u611F\u6168\u548C\u8BC4\u8BBA\u3002</p><p>\u5F88\u591A\u4EBA\u4F1A\u628A\u8FD9\u79CD\u5F00\u59CB\u52AA\u529B\u7684\u6837\u5B50\uFF0C\u5F53\u505A\u4E00\u4EF6\u53EF\u4EE5\u70AB\u8000\u7684\u4E8B\u60C5\u3002</p><p>\u4F46\u662F\u4ED6\u4EEC\u6CA1\u660E\u767D\uFF0C\u8FD9\u79CD\u5F00\u59CB\u65F6\u7684\u6837\u5B50\uFF0C\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u6BEB\u4E0D\u8D39\u529B\u7684\u505A\u51FA\u6765\u3002</p><p>\u552F\u6709\u575A\u6301\u548C\u7ED3\u679C\uFF0C\u624D\u662F\u96BE\u5F97\u7684\u3002</p><p><strong>2\u3001\u4F60\u8D8A\u662F\u5728\u610F\u4E00\u4EF6\u4E1C\u897F\uFF0C\u4F60\u5C31\u8D8A\u5BB9\u6613\u88AB\u5B83\u7ED1\u67B6\u548C\u56F0\u6270\u3002</strong></p><p>\u5F53\u4F60\u5B8C\u5168\u4E0D\u5728\u610F\u7684\u65F6\u5019\uFF0C\u5B83\u5C31\u6C38\u8FDC\u65E0\u6CD5\u5F71\u54CD\u548C\u5E72\u6270\u4F60\u3002</p><p>\u6BD4\u5982\uFF0C\u5F53\u4F60\u6CA1\u6709\u9053\u5FB7\u7684\u65F6\u5019\uFF0C\u5C31\u6CA1\u6709\u4EBA\u53EF\u4EE5\u9053\u5FB7\u7ED1\u67B6\u4F60\uFF1B\u5F53\u4F60\u4E0D\u4E70\u623F\u7684\u65F6\u5019\uFF0C\u623F\u4EF7\u6DA8\u7684\u518D\u5FEB\u4F60\u90FD\u65E0\u611F\u3002</p><p><strong>3\u3001\u62C9\u9ED1\u4E00\u4E2A\u4EBA\uFF0C\u8FDC\u6BD4\u8BF4\u670Dta\u6765\u7684\u7B80\u5355\u9AD8\u6548\u3002</strong></p><p>\u800C\u4E14\u2014\u2014\u4F60\u8BF4\u670D\u4ED6\u6709\u4EC0\u4E48\u7528\uFF1F</p><p>\u6D6A\u8D39\u81EA\u5DF1\u7684\u65F6\u95F4\uFF1F\u8BA9\u4ED6\u660E\u767D\u771F\u7406\uFF1F</p><p><strong>4\u3001\u9664\u53BB\u4F60\u7684\u7236\u6BCD\u3001\u4EB2\u621A\uFF0C\u6CA1\u6709\u51E0\u4E2A\u4EBA\u771F\u5FC3\u5E0C\u671B\u4F60\u8FC7\u5F97\u597D\uFF0C\u4E5F\u6CA1\u6709\u4EBA\u771F\u7684\u5173\u5FC3\u4F60\u7684\u5185\u5FC3\u611F\u53D7\u548C\u53D1\u751F\u5728\u4F60\u8EAB\u4E0A\u7684\u4E8B\u60C5\u3002</strong></p><p>\u4ED6\u4EEC\u53E3\u4E2D\u7684\u201C\u600E\u4E48\u4E86\uFF1F\u201D\u53EA\u662F\u8BD5\u63A2\u6027\u5730\u8BE2\u95EE\uFF0C\u5E76\u6CA1\u6709\u771F\u6B63\u60F3\u5E2E\u5FD9\u7684\u610F\u601D\uFF0C\u5C31\u50CF\u542C\u6545\u4E8B\uFF0C\u770B\u7535\u89C6\u5267\u90A3\u6837\uFF0C\u4EC5\u4EC5\u662F\u60F3\u4E86\u89E3\u4E00\u4E0B\u540E\u9762\u7684\u5267\u60C5\u53D1\u5C55\u3002</p><p><strong>5\u3001\u5E74\u8F7B\u7684\u65F6\u5019\u5F80\u5F80\u4F1A\u89C9\u5F97\u81EA\u5DF1\u8EAB\u4F53\u5F88\u597D\uFF0C\u4E8E\u662F\u4F5C\u6B7B\u4E00\u6837\u7684\u71AC\u591C\u3001\u4E0D\u5403\u65E9\u996D\u3001\u559D\u51B7\u996E\u3002</strong></p><p>\u4F46\u4EBA\u4F53\u7684\u5F88\u591A\u635F\u4F24\u90FD\u662F\u4E0D\u53EF\u9006\u7684\uFF0C\u4F60\u7684\u8EAB\u4F53\u4E00\u76F4\u90FD\u5728\u7ED9\u4F60\u7684\u884C\u4E3A\u8BB0\u8D26\uFF0C\u7B49\u4F60\u8001\u4E86\uFF0C\u8FDF\u65E9\u8981\u8FD8\u3002**</p><p>\u4E0D\u7528\u8BF4\u8EAB\u4F53\u7684\u635F\u4F24\uFF0C\u4F60\u60F3\u60F3\u80D6\u4E86\u4EE5\u540E\u518D\u7626\u56DE\u53BB\u6709\u591A\u96BE\uFF0C\u4F60\u5C31\u4E0D\u96BE\u7406\u89E3\u8EAB\u4F53\u635F\u4F24\u6709\u591A\u96BE\u6062\u590D\u3002</p><p><strong>6\u3001\u6CA1\u6709\u4EFB\u4F55\u4E00\u4EFD\u611F\u60C5\u503C\u5F97\u4F60\u653E\u5F03\u81EA\u5DF1\u7684\u524D\u7A0B\u3002</strong></p><p>\u4E8C\u5341\u5577\u5F53\u5C81\u7684\u7231\u60C5\uFF0C\u4E0D\u5FC5\u8D4C\u4E0A\u81EA\u5DF1\u7684\u4E00\u751F\u3002</p><p>\u66F4\u4F55\u51B5\u8FD9\u4E2A\u4EBA\u5927\u6982\u7387\u6700\u7EC8\u4E0D\u662F\u4F60\u7684\u53E6\u4E00\u534A\u3002</p><p><strong>7\u3001\u60C5\u7EEA\u4E0A\u7684\u7EA0\u7ED3\u5927\u591A\u662F\u56E0\u4E3A\u81EA\u5DF1\u65E2\u4E0D\u613F\u610F\u4E3A\u6539\u53D8\u4ED8\u51FA\u52AA\u529B\uFF0C\u4E5F\u4E0D\u60F3\u5E73\u5FC3\u9759\u6C14\u5730\u63A5\u53D7\u73B0\u72B6\uFF0C\u53EA\u4F1A\u5728\u539F\u5730\u7EA0\u7ED3\u548C\u62B1\u6028\u3002</strong></p><p><strong>8\u3001\u7EDD\u5927\u591A\u6570\u4EBA\u7684\u4E00\u751F\u90FD\u4F1A\u5EB8\u5EB8\u788C\u788C\u3001\u4E00\u4E8B\u65E0\u6210\uFF0C\u4ED6\u4EEC\u7684\u4EBA\u751F\u5F80\u5F80\u4F1A\u88AB\u8FD9\u4E09\u6837\u4E1C\u897F\u675F\u7F1A\u4F4F\uFF1A\u623F\u5B50\u3001\u8F66\u5B50\u3001\u5B69\u5B50\u3002</strong></p><p>\u4F60\u53EF\u4EE5\u4E0D\u62DC\u91D1\uFF0C\u4F46\u662F\u4F60\u4E0D\u5F97\u4E0D\u660E\u767D\u4E00\u4E2A\u9053\u7406\uFF1A\u53EA\u6709\u4F60\u8863\u98DF\u65E0\u5FE7\u7684\u65F6\u5019\uFF0C\u4F60\u624D\u80FD\u53BB\u8FC7\u81EA\u5DF1\u60F3\u8981\u7684\u751F\u6D3B\u3002</p><p><strong>9\u3001\u4EBA\u751F\u5728\u4E16\uFF0C\u96BE\u514D\u4F1A\u201C\u5403\u5C4E\u201D\uFF0C\u4F46\u662F\u5343\u4E07\u4E0D\u8981\u7EC6\u56BC\uFF0C\u90A3\u4E9B\u50BBx\u7684\u884C\u4E3A\uFF0C\u8FC7\u53BB\u5C31\u8FC7\u53BB\u4E86\uFF0C\u653E\u8FC7\u81EA\u5DF1\u3002</strong></p><p><strong>10\u3001\u5E74\u8F7B\u4EBA\u6700\u5BB9\u6613\u51FA\u73B0\u7684\u95EE\u9898\u5C31\u662F\u52AA\u529B\u4E00\u70B9\u70B9\u5C31\u60F3\u770B\u5230\u6536\u83B7\u548C\u7ED3\u679C\u3002</strong></p><p>\u5C31\u50CF\u5C0F\u5B69\u5B50\u73A9\u73A9\u5177\u4E00\u6837\uFF0C\u6441\u4E00\u4E0B\u6309\u94AE\uFF0C\u5C31\u7ACB\u523B\u60F3\u770B\u5230\u73A9\u5177\u7684\u56DE\u9988\u3002</p><p>\u5982\u679C\u6CA1\u6709\u56DE\u9988\uFF0C\u90A3\u5C31\u662F\u73A9\u5177\u574F\u4E86\u3002</p><p>\u4F46\u4EBA\u751F\u7684\u53CD\u9988\u673A\u5236\u662F\u6781\u5176\u590D\u6742\u7684\uFF0C\u90A3\u4E9B\u53EF\u4EE5\u5373\u65F6\u53CD\u9988\u7684\u4E8B\u4EF6\uFF0C\u90FD\u662F\u5BF9\u4F60\u6709\u767E\u5BB3\u800C\u65E0\u4E00\u5229\u7684\uFF0C\u6BD4\u5982\u8D4C\u535A\u3001\u5237\u6296\u97F3\u3001\u770B\u723D\u6587\u3002</p><p>\u800C\u8BF8\u5982\u770B\u4E66\u3001\u5065\u8EAB\u3001\u65C5\u884C\u3001\u5B66\u4E60\u8FD9\u4E9B\u53EF\u4EE5\u7ED9\u4F60\u5E26\u6765\u957F\u671F\u56DE\u62A5\u4E8B\u60C5\uFF0C\u4F60\u53EF\u80FD\u52AA\u529B\u5341\u5929\u90FD\u4E1D\u6BEB\u611F\u89C9\u4E0D\u5230\u5B83\u7684\u4E00\u4E01\u70B9\u53CD\u9988\u3002</p><p><strong>11\u3001\u806A\u660E\u8FD9\u79CD\u54C1\u8D28\uFF0C\u5728\u6210\u529F\u7684\u8981\u7D20\u4E2D\uFF0C\u5360\u5230\u7684\u6BD4\u91CD\u4E0D\u8D85\u8FC720%\u3002</strong></p><p>\u6267\u884C\u529B\u3001\u610F\u5FD7\u529B\u3001\u81EA\u63A7\u80FD\u529B\uFF0C\u8FD9\u4E9B\u6BD4\u806A\u660E\u6765\u7684\u91CD\u8981\u7684\u591A\u3002</p><p><strong>12\u3001\u6211\u4EEC\u7ECF\u5E38\u8BA4\u4E3A\u6211\u4EEC\u81EA\u5DF1\u53EF\u4EE5\u4E3B\u5BB0\u6211\u4EEC\u7684\u8EAB\u4F53\u548C\u884C\u4E3A\uFF0C\u5176\u5B9E\u8FD9\u79CD\u60F3\u6CD5\u662F\u5927\u8111\u5728\u8BEF\u5BFC\u6211\u4EEC\u3002</strong></p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5927\u8111\u7ECF\u5E38\u4F1A\u8FDB\u884C\u81EA\u6211\u9003\u907F\uFF0C\u6BD4\u5982\u5F53\u4F60\u51C6\u5907\u53BB\u7814\u7A76\u521B\u4E1A\u7684\u65F6\u5019\uFF0C\u5927\u8111\u7684\u6307\u4EE4\u7ECF\u5E38\u662F\u201C\u4E0D\u8981\u53BB\uFF0C\u6CA1\u6709\u7528\uFF0C\u8D5A\u4E0D\u5230\u94B1\u201D\uFF0C\u4F46\u5176\u5B9E\u4F60\u4ED4\u7EC6\u60F3\u60F3\uFF0C\u4E3A\u4EC0\u4E48\u5927\u8111\u4F1A\u5728\u6CA1\u6709\u4EFB\u4F55\u884C\u4E1A\u77E5\u8BC6\u548C\u5E02\u573A\u8C03\u7814\u7684\u60C5\u51B5\u4E0B\u505A\u51FA\u5426\u5B9A\u7684\u5224\u65AD\uFF1F</p><p>\u56E0\u4E3A\u5B83\u8FD9\u6837\u5C31\u53EF\u4EE5\u907F\u514D\u4E86\u81EA\u5DF1\u53BB\u5C1D\u8BD5\u548C\u601D\u8003\uFF0C\u5C31\u53EF\u4EE5\u7EE7\u7EED\u7528\u7B80\u5355\u6A21\u5F0F\u751F\u6D3B\u4E0B\u53BB\u3002</p><p>\u8FD9\u6837\u4F60\u7684\u8EAB\u4F53\u5C31\u65E0\u6CD5\u53BB\u4EAB\u53D7\u66F4\u597D\u7684\uFF0C\u4F46\u8FD9\u5BF9\u4F60\u7684\u5927\u8111\u5E76\u6CA1\u6709\u591A\u5927\u5F71\u54CD\uFF0C\u5B83\u53EA\u4F1A\u66F4\u8F7B\u677E\u3002</p><p>\u6240\u4EE5\uFF0C\u5F53\u4F60\u518D\u6709\u4EC0\u4E48\u60F3\u6CD5\u7684\u65F6\u5019\uFF0C\u4E0D\u8981\u7528\u201C\u7EA2\u706F\u601D\u7EF4\u201D\u6765\u601D\u8003\uFF0C\u4F60\u4E0D\u53BB\u770B\u770B\u4F60\u5C31\u77E5\u9053\u90A3\u91CC\u7684\u666F\u8272\u4E48\uFF1F</p><p>\u540C\u65F6\uFF0C\u5927\u8111\u8FD8\u662F\u81EA\u79C1\u7684\uFF0C\u5F53\u4F60\u6291\u90C1\u7684\u65F6\u5019\uFF0C\u4F60\u7684\u8EAB\u4F53\u6CA1\u6709\u53D7\u5230\u4EC0\u4E48\u4F24\u5BB3\uFF0C\u4F46\u4F60\u4ECD\u7136\u60F3\u81EA\u6740\u3002</p><p>\u56E0\u4E3A\u5927\u8111\u60F3\u7528\u7ED3\u675F\u751F\u547D\u7684\u65B9\u5F0F\u6765\u7ED3\u675F\u5B83\u7684\u75DB\u82E6\u3002</p><p><strong>13\u3001\u94B1\u8FD9\u79CD\u4E1C\u897F\uFF0C\u4E00\u70B9\u513F\u4E5F\u4E0Dlow\u3002</strong></p><p>\u8C08\u94B1\u4E5F\u4E0D\u4F1A\u4F24\u611F\u60C5\uFF0C\u771F\u6B63\u4F24\u611F\u60C5\u7684\u662F\u6253\u7740\u201C\u611F\u60C5\u201D\u7684\u5E4C\u5B50\u60F3\u5360\u5BF9\u65B9\u4FBF\u5B9C\u7684\u884C\u4E3A\u3002</p><p>\u94B1\uFF0C\u90FD\u662F\u7528\u81EA\u5DF1\u7684\u89C1\u8BC6\u3001\u80FD\u529B\u8D5A\u5230\u7684\uFF0C\u82B1\u7684\u65F6\u5019\u5E94\u8BE5\u611F\u89C9\u5230\u9A84\u50B2\u624D\u5BF9\u3002</p><p><strong>14\u3001\u4EBA\u90FD\u662F\u5E26\u7740\u9762\u5177\u751F\u6D3B\u7684\uFF0C\u8FD9\u79CD\u9762\u5177\u662F\u4E00\u79CD\u793E\u4F1A\u5C5E\u6027\u3002</strong></p><p>\u5982\u679C\u4F60\u628A\u9762\u5177\u5168\u90FD\u5378\u4E0B\u6765\uFF0C\u90A3\u4E48\u6240\u6709\u7684\u4EBA\u90FD\u662F\u5351\u52A3\u7684\uFF0C\u4ED6\u4EEC\u8D2A\u5A6A\u3001\u81EA\u79C1\u3001\u5584\u53D8\u3001\u597D\u8272\u3001\u61D2\u60F0\uFF0C\u559C\u6B22\u8FFD\u6C42\u523A\u6FC0\uFF0C\u559C\u6B22\u4E0D\u52B3\u800C\u83B7\u3002</p><p>\u6240\u4EE5\u4EBA\u6027\u6839\u672C\u5C31\u7ECF\u4E0D\u8D77\u4EFB\u4F55\u8003\u9A8C\uFF0C\u518D\u597D\u7684\u670B\u53CB\uFF0C\u4E5F\u53EF\u80FD\u4F1A\u7FFB\u8138\u3002</p><p>\u4F60\u80FD\u505A\u7684\uFF0C\u5C31\u662F\u5BF9\u4EFB\u4F55\u4EBA\u90FD\u4F5C20%\u7684\u4FDD\u7559\uFF0C\u4FDD\u62A4\u597D\u81EA\u5DF1\u3002</p><p><strong>15\u3001\u8FD9\u4E16\u754C\u4E0A\u771F\u6B63\u8DDF\u4F60\u6709\u5173\u7CFB\u7684\u4E8B\u513F\u5F88\u5C11\u3002</strong></p><p>\u5982\u679C\u4F60\u5B66\u4F1A\u628A\u81EA\u5DF1\u7684\u65F6\u95F4\u548C\u7CBE\u529B\u90FD\u7528\u5728\u548C\u81EA\u5DF1\u6709\u5173\u7CFB\u7684\u4E8B\u513F\u4E0A\uFF0C\u60F3\u5FC5\u4F60\u4F1A\u6BD4\u73B0\u5728\u4F18\u79C0\u5F88\u591A\u3002</p><p><strong>16\u3001\u5982\u679C\u4F60\u771F\u7684\u60F3\u4EAB\u53D7\u66F4\u597D\u7684\u4E1C\u897F\uFF0C\u90A3\u4F60\u6B64\u65F6\u6B64\u523B\u6700\u5E94\u8BE5\u505A\u7684\u5C31\u662F\u5FCD\u53D7\u3002</strong></p><p>\u5FCD\u53D7\u81EA\u5DF1\u6BD4\u522B\u4EBA\u4ED8\u51FA\u66F4\u591A\u3002</p><p>\u5FCD\u53D7\u81EA\u5DF1\u6682\u65F6\u65E0\u6CD5\u4EAB\u53D7\u3002</p><p>\u5FCD\u53D7\u81EA\u5DF1\u4E0D\u80FD\u53BB\u8FFD\u6C42\u90A3\u4E9B\u523A\u6FC0\u548C\u6B22\u6109\u3002</p><p>\u4F46\u5F53\u4F60\u5F97\u5230\u7684\u56DE\u62A5\u7684\u65F6\u5019\uFF0C\u4F60\u5C31\u4F1A\u89C9\u5F97\uFF0C\u4E00\u5207\u7B49\u5F85\u90FD\u662F\u503C\u5F97\u7684\u3002</p><p>\u8FD9\u4E00\u6761\u5F88\u9E21\u6C64\uFF0C\u4F46\u7EDD\u5BF9\u771F\u5B9E\uFF0C\u4E5F\u503C\u5F97\u4F60\u4E00\u4E2A\u8D5E\u3002</p><p><strong>17\u3001\u4E00\u4E2A\u4EBA\u4E00\u751F\u4E4B\u4E2D\u80FD\u6539\u53D8\u81EA\u5DF1\u547D\u8FD0\u7684\u673A\u4F1A\u5E76\u4E0D\u591A\u3002</strong></p><p>\u65E0\u8BBA\u662F\u9AD8\u8003\u3001\u725B\u5E02\u8FD8\u662F\u6295\u8D44\uFF0C\u4E5F\u8BB8\u9519\u8FC7\u4E00\u6B21\uFF0C\u4F60\u5C31\u518D\u4E5F\u6CA1\u6709\u673A\u4F1A\u8DE8\u8D8A\u81EA\u5DF1\u7684\u9636\u5C42\u4E86\u3002</p><p>\u4E0D\u8981\u89C9\u5F97\u8FD9\u5F88\u4E0D\u516C\u5E73\uFF0C\u4F60\u7FA1\u6155\u7684\u90A3\u4E9B\u5BCC\u4E8C\u4EE3\uFF0C\u4E5F\u4E0D\u8FC7\u662F\u4E0A\u4E00\u4EE3\u4EBA\u628A\u63E1\u4F4F\u4E86\u673A\u4F1A\u3002</p><p><strong>18\u3001\u5F88\u591A\u4EBA\u4E4B\u6240\u4EE5\u4F1A\u81EA\u6211\u611F\u52A8\uFF0C\u662F\u56E0\u4E3A\u4ED6\u4EEC\u603B\u559C\u6B22\u7528\u81EA\u5DF1\u7684\u4ED8\u51FA\u6765\u8981\u6C42\u5BF9\u65B9\u7684\u53CD\u9988\u3002</strong></p><p>\u4ED6\u4EEC\u4E0D\u660E\u767D\uFF0C\u4F60\u662F\u4F60\uFF0C\u5979\u662F\u5979\u3002</p><p>\u4F60\u559C\u6B22\u9694\u58C1\u8001\u738B\uFF0C\u4ED6\u559C\u6B22\u5B69\u5B50\u4ED6\u5988\u3002</p><p>\u4F60\u4ED8\u51FA\u518D\u591A\uFF0C\u4E0D\u4EE3\u8868\u6211\u4F1A\u89C9\u5F97\u6709\u83B7\u5F97\u611F\u3002</p><p>\u4F60\u8D39\u5C3D\u5343\u8F9B\u4E07\u82E6\u7ED9\u6211\u9001\u6765\u4E00\u5768\u5C4E\uFF0C\u6211tm\u8FD8\u8981\u611F\u6FC0\u4F60\u4E0D\u6210\uFF1F</p><p><strong>19\u3001\u65E0\u8BBA\u4F60\u9047\u5230\u4EC0\u4E48\u6837\u7684\u4E0D\u516C\u5E73\u5F85\u9047\uFF0C\u4F60\u90FD\u4E0D\u8981\u671F\u5F85\u6709\u522B\u4EBA\u4F1A\u7AD9\u51FA\u6765\u66FF\u4F60\u51FA\u5934\u3002</strong></p><p>\u5F53\u4E8B\u4E0D\u5173\u5DF1\u7684\u65F6\u5019\uFF0C\u6CA1\u6709\u4EBA\u4F1A\u4E3A\u4E86\u4F60\u7684\u5229\u76CA\u53BB\u8BA9\u81EA\u5DF1\u9677\u5165\u4E0D\u5229\u3002</p><p><strong>20\u3001\u5E73\u51E1\u5E76\u4E0D\u53EF\u8D35\uFF0C\u4F46\u662F\u5F53\u4F60\u65E0\u6CD5\u6539\u53D8\u547D\u8FD0\u7684\u65F6\u5019\uFF0C\u5C31\u8981\u63A5\u53D7\u73B0\u5B9E\u3002</strong></p><p>\u4F60\u670999.9%\u7684\u53EF\u80FD\u4E00\u8F88\u5B50\u90FD\u4F1A\u662F\u4E2A\u51E1\u4EBA\uFF0C\u4F60\u80FD\u600E\u4E48\u6837\uFF1F\u5C31\u5730\u6253\u6EDA\uFF1F\u4E0D\u63A5\u53D7\uFF1F</p><p>\u7136\u540E\u5462\uFF1F\u56E0\u4E3A\u81EA\u5DF1\u7684\u5E73\u51E1\u800C\u62B1\u6068\u7EC8\u8EAB\uFF1F\u628A\u81EA\u5DF1\u5F04\u6210\u6291\u90C1\u75C7\uFF1F</p><p>\u9664\u4E86\u6298\u817E\u4F60\u81EA\u5DF1\uFF0C\u4F60\u8FD8\u4F1A\u70B9\u522B\u7684\u4E48\uFF1F</p>",68),g=[n];function e(c,_){return t(),o("div",null,g)}const l=p(s,[["render",e],["__file","2020-07-03-zhi-hu-dao-li.html.vue"]]);export{l as default};
