import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as a,a as p,b as e,d as n,e as s,r as c}from"./app.6f7e30ef.js";const i={},h={href:"https://www.toutiao.com/i6894410680401658379",target:"_blank",rel:"noopener noreferrer"},d=s('<p>\u8FD9\u4E2A\u95EE\u9898\u672C\u6765\u662F\u5728\u77E5\u4E4E\u56DE\u7B54\u7684\u4E00\u4E2A\u95EE\u9898\uFF0C\u5F53\u65F6\u968F\u4FBF\u8BF4\u4E86\u51E0\u53E5\uFF0C\u89C9\u5F97\u6CA1\u8BF4\u900F\uFF0C\u4ECA\u5929\u62FF\u5230\u516C\u53F7\u6765\u8BE6\u7EC6\u8BF4\u4E0B\u3002</p><p>\u9996\u5148\u8981\u8BF4\uFF0C\u8FD9\u4E2A\u8BDD\u9898\u591A\u591A\u5C11\u5C11\u6709\u70B9\u4F2A\u79D1\u5B66\uFF0C\u56E0\u4E3A\u5E76\u4E0D\u662F\u6240\u6709\u6210\u529F\u4EBA\u58EB\u90FD\u4E0D\u4EAB\u53D7\u4EBA\u751F\u5929\u5929\u52A0\u73ED\u3002\u4F46\u786E\u5B9E\u4E5F\u6709\u5F88\u591A\u4EBA\u6536\u5165\u5BB6\u4EA7\u5DF2\u7ECF\u5F88\u9AD8\u4E86\uFF0C\u4F46\u4F9D\u65E7\u5929\u5929\u5FD9\u5F97\u8DDF\u4E2A\u9640\u87BA\u4F3C\u7684\u3002</p><p>\u800C\u4E14\u8FD9\u4E2A\u8BDD\u9898\u4F3C\u4E4E\u6709\u70B9\u591A\u4F59\uFF0C\u6BD5\u7ADF\u5927\u90E8\u5206\u4EBA\u6CA1\u6CD5\u968F\u968F\u4FBF\u4FBF\u6210\u529F\uFF0C\u90FD\u662F\u62FC\u8FC7\u6765\u7684\uFF0C\u4F24\u4EA1\u6389\u4E00\u6279\u540E\uFF0C\u5269\u4E0B\u7684\u7406\u6240\u5F53\u7136\u662F\u5F88\u80FD\u5403\u82E6\u7684\u3002</p><p>\u4E0D\u8FC7\u6211\u4ECA\u5929\u663E\u7136\u4E0D\u60F3\u53EA\u804A\u4E00\u822C\u539F\u56E0\uFF0C\u54B1\u4EEC\u804A\u70B9\u522B\u4EBA\u8BF4\u5F97\u4E0D\u591A\u7684\uFF0C\u4E5F\u5E0C\u671B\u5927\u5BB6\u770B\u5B8C\u672C\u6587\u80FD\u6709\u70B9\u542F\u53D1\u3002</p><h2 id="_1\u3001\u865A\u5984\u7684\u5B89\u5168\u611F" tabindex="-1"><a class="header-anchor" href="#_1\u3001\u865A\u5984\u7684\u5B89\u5168\u611F" aria-hidden="true">#</a> 1\u3001\u865A\u5984\u7684\u5B89\u5168\u611F</h2><p>\u5148\u8BF4\u4E0B\u6211\u7ECF\u5386\u7684\u4E00\u4EF6\u4E8B\u3002\u6211\u65E9\u5E74\u7684\u4E00\u4E2A\u5144\u5F1F\u4E4B\u524D\u88AB\u4E92\u8054\u7F51\u5927\u5382\u7ED9\u8D76\u51FA\u6765\u4E86\u3002</p><p>\u4ED6\u8DDF\u6211\u8BF4\u5728\u88AB\u8D76\u51FA\u6765\u4E4B\u524D\uFF0C\u4ED6\u4E00\u76F4\u4EE5\u4E3A\u81EA\u5DF1\u5F88\u201C\u5B89\u5168\u201D\u3002\u9879\u76EE\u6309\u90E8\u5C31\u73ED\u5730\u5728\u505A\uFF0C\u8BE5\u5E72\u5565\u5E72\u5565\uFF0C\u7A81\u7136\u6709\u4E00\u5929\u6536\u5230\u6D88\u606F\uFF0C\u8BF4\u662F\u4EA7\u54C1\u7EBF\u88C1\u6389\u4E86\uFF0C\u4ED6\u4EEC\u8FD9\u4E9B\u4EBA\u5168\u88AB\u5408\u5E76\u5230\u53E6\u4E00\u6761\u4EA7\u7EBF\u53BB\u4E86\uFF0C\u7136\u540E\u95EE\u9898\u5C31\u5728\u8FD9\u4E2A\u201C\u5408\u5E76\u201D\u4E0A\u3002</p><p>\u5408\u5E76\u540E\u6240\u6709\u5458\u5DE5\u5230\u4E86\u53E6\u4E00\u6761\u4EA7\u7EBF\u90FD\u8981\u91CD\u65B0\u5B89\u6392\u5C97\u4F4D\uFF0C\u5BF9\u4E8E\u90A3\u4E9B\u57FA\u5C42\u5E72\u7F16\u7801\u7684\u5C0F\u4F19\u4F34\u6765\u8BF4\uFF0C\u8FD9\u4E2A\u4E8B\u672C\u8EAB\u4E0D\u662F\u95EE\u9898\uFF0C\u5728\u54EA\u5E72\u90FD\u662F\u5E72\uFF0C\u65E0\u975E\u662F\u6362\u4E2A\u5751\u3002\u6700\u5012\u9709\u7684\u5C31\u662F\u6211\u5144\u5F1F\u4ED6\u4EEC\u8FD9\u79CD\u4E2D\u5C42\u5E72\u90E8\u3002</p><p>\u4F17\u6240\u5468\u77E5\uFF0C\u5927\u5382\u91CC\u5565\u90FD\u7F3A\uFF0C\u4F46\u662F\u552F\u72EC\u4E0D\u7F3A\u9886\u5BFC\uFF0C\u9886\u5BFC\u6BD4\u516C\u53F8\u7684\u76C6\u683D\u90FD\u591A\u3002\u6240\u4EE5\u4ED6\u4EEC\u8FD9\u4E9B\u9886\u5BFC\u5230\u4E86\u53E6\u4E00\u6761\u4EA7\u7EBF\u5C31\u6BD4\u8F83\u5C34\u5C2C\uFF0C\u65E2\u6CA1\u6CD5\u8BA9\u4ED6\u4EEC\u7EE7\u7EED\u5F53\u9886\u5BFC\uFF08\u6BD5\u7ADF\u4EBA\u5BB6\u53C8\u4E0D\u7F3A\u9886\u5BFC\uFF09\uFF0C\u4E5F\u6CA1\u6CD5\u5B89\u6392\u4ED6\u4EEC\u53BB\u7F16\u7801\uFF0C\u6BD5\u7ADF\u8FD9\u4E9B\u505A\u7BA1\u7406\u7684\u591A\u5E74\u4E0D\u5199\u4EE3\u7801\uFF0C\u7F16\u7801\u6C34\u5E73\u8DDF\u4E2A\u5927\u5B66\u751F\u5DEE\u4E0D\u591A\uFF0C\u800C\u4E14\u8BA9\u4ED6\u4EEC\u71AC\u591C\u4FEEbug\u4ED6\u4EEC\u5FC3\u6001\u4E5F\u4E0D\u592A\u597D\u3002</p><p>\u4E0D\u53EA\u8FD9\u4E2A\u95EE\u9898\uFF0C\u4ED6\u4EEC\u7BA1\u7406\u5C42\u5DE5\u8D44\u8981\u6BD4\u57FA\u5C42\u9AD8\u5F97\u591A\uFF0C\u516C\u53F8\u4E0D\u53EF\u80FD\u8BA9\u4ED6\u4EEC\u62FF\u7740\u8FD9\u4E48\u9AD8\u5DE5\u8D44\u53BB\u505A\u7F16\u7801\u7684\u5DE5\u4F5C\u3002</p><p>\u8FD9\u5C31\u5C34\u5C2C\u4E86\uFF0C\u540E\u6765\u516C\u53F8\u7ECF\u8FC7\u4E25\u8083\u8BA8\u8BBA\uFF0C\u7ED9\u4E86\u4ED6\u4EEC\u4E24\u6761\u8DEF\uFF1A</p><p>\u5982\u679C\u4E0D\u964D\u7EA7\uFF0C\u5C31\u53BB\u6D77\u5916\u5E2E\u516C\u53F8\u53BB\u5F00\u7586\u6269\u571F\uFF0C\u6269\u5C55\u4E1A\u52A1\uFF0C\u6BD4\u8F83\u8270\u82E6\uFF0C\u8981\u79BB\u5BB6\uFF0C\u8981\u627F\u53D7\u5DE8\u5927\u7684\u538B\u529B\uFF0C\u5EFA\u8BAE\u4F18\u5148\u9009\u62E9\u3002</p><p>\u8981\u4E0D\u5C31\u964D\u7EA7\uFF0C\u53BB\u505A\u57FA\u5C42\u5458\u5DE5\uFF0C\u6536\u5165\u5927\u5E45\u524A\u51CF\uFF0C\u4ECE\u57FA\u5C42\u5E72\u8D77\uFF0C\u5C06\u6765\u6709\u7BA1\u7406\u5C97\u4F18\u5148\u63D0\u62D4\u4ED6\uFF08\u5927\u5382\u91CC\u4E13\u7528\u8BDD\u672F\uFF0C\u8DDF\u6BCF\u4E2A\u4EBA\u90FD\u8BF4\u4F60\u5728\u4F60\u4EEC\u8FD9\u4E2A\u5C42\u7EA7\u91CC\u7B97\u9760\u524D\u7684\uFF0C\u4E0B\u6B21\u4F18\u5148\u8003\u8651\u4F60\uFF09\uFF0C\u5E76\u4E14\u76F8\u4FE1\u4ED6\u7684\u80FD\u529B\u5728\u65B0\u7684\u5C97\u4F4D\u4E0A\u5F88\u5FEB\u5C31\u4F1A\u6709\u7A81\u7834\uFF0Cblablabla\u3002</p><p>\u6211\u8FD9\u4E2A\u5144\u5F1F\u89C9\u5F97\u6C14\u4E0D\u8FC7\uFF0C\u65E2\u4E0D\u60F3\u53BB\u6D77\u5916\uFF08\u4E00\u628A\u5E74\u7EAA\u4E86\uFF09\uFF0C\u53C8\u63A5\u53D7\u4E0D\u4E86\u518D\u56DE\u53BB\u5199\u4EE3\u7801\uFF0C\u5C31\u8F9E\u804C\u4E86\u3002\u8F9E\u804C\u4E4B\u540E\u6B63\u7ECF\u614C\u4E86\u4E00\u6BB5\u65F6\u95F4\uFF0C\u540E\u6765\u53BB\u53E6\u4E00\u4E2A\u670B\u53CB\u7684\u521B\u4E1A\u516C\u53F8\u5E26\u56E2\u961F\u4E86\uFF0C\u6536\u5165\u5C11\u4E86\u4E00\u4E9B\u3002</p><p>\u524D\u6BB5\u65F6\u95F4\u7EA6\u996D\u4ED6\u8BF4\u4E86\u4E24\u4EF6\u4E8B\uFF1A</p><p>\u7B2C\u4E00\u4EF6\u5C31\u662F\u6211\u4EE5\u524D\u4E3E\u7684\u90A3\u4E2A\u4F8B\u5B50\uFF0C\u7F8E\u56FD\u90A3\u8FB9\u5F88\u591A\u7A0B\u5E8F\u5458\u6545\u610F\u4E0D\u5347\u7EA7\u4E5F\u4E0D\u6DA8\u5DE5\u8D44\uFF0C\u4E2D\u56FD\u4EBA\u8D8B\u4E4B\u82E5\u9E5C\u7684\u201C\u7BA1\u7406\u5C42\u201D\uFF0C\u4ED6\u4EEC\u6B7B\u6D3B\u4E0D\u53BB\uFF0C\u4E3B\u8981\u662F\u56E0\u4E3A\u516C\u53F8\u51FA\u4E86\u95EE\u9898\uFF0C\u7B2C\u4E00\u6279\u88C1\u7684\u5C31\u662F\u5DE5\u8D44\u9AD8\u4EA7\u51FA\u4E0D\u660E\u7684\u7BA1\u7406\u5C42\u3002\u800C\u4E14\u7BA1\u7406\u5C42\u5F80\u5F80\u6CA1\u6709\u6280\u672F\u9632\u8EAB\uFF0C\u88AB\u8D76\u51FA\u6765\u614C\u5F97\u4E00\u6279\u3002\u4ED6\u4EE5\u524D\u6CA1\u61C2\u8FD9\u4E8B\uFF0C\u73B0\u5728\u7A81\u7136\u660E\u767D\u4E86\u3002</p><p>\u7B2C\u4E8C\u4EF6\u4E8B\u662F\u4ED6\u65E9\u5E74\u6709\u4E2A\u673A\u4F1A\u53BB\u521B\u4E1A\u516C\u53F8\uFF0C\u4F46\u662F\u89C9\u5F97\u521B\u4E1A\u516C\u53F8\u4E0D\u592A\u5B89\u5168\uFF0C\u968F\u65F6\u6709\u53EF\u80FD\u5012\u6389\uFF0C\u7F3A\u4E4F\u5B89\u5168\u611F\uFF0C\u4E0D\u5982\u53BB\u5927\u5382\u7A33\u5B9A\u3002</p><p>\u4ECE\u4E8B\u540E\u7684\u60C5\u51B5\u6765\u770B\uFF0C\u5927\u5382\u7684\u5C97\u4F4D\u5E76\u4E0D\u662F\u7A33\u5B9A\uFF0C\u800C\u662F\u4ED6\u4F5C\u4E3A\u4E00\u9897\u87BA\u4E1D\u9489\uFF0C\u4E0D\u77E5\u9053\u81EA\u5DF1\u7684\u4EA7\u54C1\u5728\u5E02\u573A\u4E0A\u7684\u72B6\u6001\uFF0C\u81EA\u5DF1\u56E0\u4E3A\u65E0\u77E5\u800C\u5145\u6EE1\u5B89\u5168\u611F\u3002</p><p>\u8FD9\u4E5F\u662F\u6211\u4EEC\u4ECA\u5929\u8981\u8BB2\u7684\u4E8B\u3002</p><p>\u5BF9\u4E8E\u5927\u90E8\u5206\u4EBA\u6765\u8BF4\uFF0C\u90FD\u5B58\u5728\u4E00\u4E2A\u201C\u865A\u5E7B\u7684\u5B89\u5168\u611F\u201D\uFF0C\u89C9\u5F97\u81EA\u5DF1\u5446\u5728\u4E00\u4E2A\u5B89\u5168\u6E2F\u91CC\uFF0C\u516C\u53F8\u4E0D\u4F1A\u5012\u95ED\uFF0C\u5C97\u4F4D\u4E0D\u4F1A\u88C1\u64A4\uFF0C\u5E02\u573A\u975E\u5E38\u7A33\u5B9A\uFF0C\u6BCF\u5929\u8D77\u6765\u90FD\u8DDF\u6628\u5929\u5DEE\u4E0D\u591A\u3002</p><p>\u771F\u5B9E\u7684\u60C5\u51B5\u80AF\u5B9A\u4E0D\u662F\u8FD9\u6837\u7684\u561B\uFF0C\u6700\u8D77\u7801\u4E00\u70B9\uFF0C<strong>\u5E02\u573A\u662F\u4E0D\u65AD\u6CE2\u52A8\u7684</strong>\uFF0C\u516C\u53F8\u5728\u5E02\u573A\u8FD9\u4E2A\u5927\u6D77\u91CC\u5C31\u8DDF\u4E00\u53F6\u6241\u821F\u822A\u884C\u5728\u6CE2\u6D9B\u6C79\u6D8C\u7684\u5927\u6D77\u4E0A\u4E00\u6837\u3002</p><p><strong>\u53EA\u662F\u7AD9\u5728\u7532\u677F\u4E0A\u7684\u8239\u957F\u80FD\u770B\u5230\u5168\u666F\uFF0C\u5E76\u4E14\u77E5\u9053\u660E\u5929\u53EF\u80FD\u662F\u4EC0\u4E48\u5929\u6C14\u3002\u4ED6\u614C\u5F97\u8981\u6B7B\uFF0C\u6BCF\u5929\u90FD\u89C9\u5F97\u65E0\u6BD4\u51F6\u9669\uFF0C\u53EF\u662F\u4E0B\u8FB9\u7684\u4EBA\u53CD\u5012\u662F\u5145\u6EE1\u5B89\u5168\u611F\u3002</strong></p><p><strong>\u8FD9\u4E2A\u610F\u4E49\u4E0A\u8BB2\uFF0C\u7AD9\u5F97\u8D8A\u9AD8\uFF0C\u770B\u5F97\u8D8A\u5168\uFF0C\u8D8A\u614C\u8D8A\u75DB\u82E6\u3002</strong></p><p><strong>\u56E0\u4E3A\u4E0D\u6B62\u8981\u5173\u5FC3\u4E1A\u52A1\u600E\u4E48\u6837\uFF0C\u8FD8\u8981\u5173\u5FC3\u5230\u671F\u7684\u503A\u52A1\u600E\u4E48\u529E\uFF0C\u6295\u653E\u7684\u5E7F\u544A\u6709\u6CA1\u6709\u6548\u679C\uFF0C\u662F\u4E0D\u662F\u6709\u5F3A\u6709\u529B\u7ADE\u4E89\u516C\u53F8\u51FA\u73B0\uFF0C\u5269\u4E0B\u7684\u73B0\u91D1\u8FD8\u80FD\u6491\u591A\u4E45\uFF0C\u4EE5\u53CA\u653F\u7B56\u53D8\u5316\u7B49\u7B49\uFF0C\u628A\u8FD9\u4E9B\u4E8B\u90FD\u8003\u8651\u8FDB\u53BB\uFF0C\u57FA\u672C\u6CA1\u4EBA\u80FD\u5B89\u5FC3\u7761\u5F97\u7740\u3002</strong></p><p><strong>\u800C\u6050\u60E7\u6BD4\u5176\u4ED6\u6240\u6709\u52A8\u673A\u90FD\u80FD\u8BA9\u4EBA\u5145\u6EE1\u52A8\u673A\u3002</strong></p><p>\u5F88\u591A\u8001\u677F\u770B\u7740\u975E\u5E38\u5149\u9C9C\uFF0C\u5176\u5B9E\u4ED6\u4EEC\u7EDD\u5927\u90E8\u5206\u90FD\u5728\u8E29\u94A2\u4E1D\uFF0C\u4E00\u6B65\u51FA\u9519\uFF0C\u51E0\u5341\u5E74\u7684\u79EF\u7D2F\u90FD\u6253\u6C34\u6F02\uFF0C\u8FD9\u79CD\u4E8B\u8FD9\u4E24\u5E74\u4F30\u8BA1\u5927\u5BB6\u4E5F\u90FD\u770B\u591A\u4E86\u3002\u5927\u90E8\u5206\u8001\u677F\u7684\u94B1\u90FD\u662F\u8D26\u9762\u4E0A\u7684\uFF0C\u5F20\u4E09\u6B20\u6211\u4E24\u4E2A\u4EBF\uFF0C\u6211\u6B20\u674E\u56DB\u4E00\u4E2A\u4EBF\uFF0C\u4ED6\u4FE9\u5BF9\u51B2\u4E0B\uFF0C\u6211\u6709\u4E00\u4EBF\u8D44\u4EA7\u3002\u5982\u679C\u78B0\u4E0A\u4ECA\u5E74\u8FD9\u79CD\u767E\u5E74\u4E00\u9047\u7684\u5927\u75AB\u60C5\uFF0C\u53EF\u80FD\u5F20\u4E09\u5012\u95ED\u4E86\uFF0C\u6211\u5C31\u4ECE\u8D44\u4EA7\u4E00\u4EBF\u53D8\u6210\u8D1F\u503A\u4E00\u4EBF\u4E86\u3002\u597D\u591A\u8001\u677F\u4ECA\u5E74\u4E00\u591C\u6210\u201C\u8D1F\u7FC1\u201D\uFF0C\u5C31\u662F\u8FD9\u4E2A\u95EE\u9898\u3002</p><p><strong>\u53CD\u5012\u662F\u201C\u4E8C\u4EE3\u201D\u4EEC\u666E\u904D\u8FC7\u5F97\u975E\u5E38\u723D\uFF0C\u4ED6\u4EEC\u8DDF\u57FA\u5C42\u5458\u5DE5\u6709\u70B9\u50CF\uFF0C\u770B\u4E0D\u5230\u5168\u5C40\uFF0C\u6709\u79CD\u865A\u5984\u7684\u5B89\u5168\u611F\uFF0C\u201C\u4E00\u4EE3\u201D\u4EEC\u5F80\u5F80\u5162\u5162\u4E1A\u4E1A\uFF0C\u52A0\u73ED\u52A0\u70B9\u3002</strong></p><h2 id="_2\u3001\u6240\u6709\u751F\u610F\u90FD\u662F\u4E00\u65F6\u7684" tabindex="-1"><a class="header-anchor" href="#_2\u3001\u6240\u6709\u751F\u610F\u90FD\u662F\u4E00\u65F6\u7684" aria-hidden="true">#</a> 2\u3001\u6240\u6709\u751F\u610F\u90FD\u662F\u4E00\u65F6\u7684</h2><p>\u8FD9\u53E5\u8BDD\u6211\u542C\u4E86\u5F88\u591A\u5E74\uFF0C\u4E00\u76F4\u4E0D\u592A\u660E\u767D\uFF0C\u56E0\u4E3A\u90A3\u4E9B\u6301\u7EED\u767E\u5E74\u7684\u4F01\u4E1A\u6709\u7684\u662F\uFF0C\u4E3A\u5565\u8BF4\u751F\u610F\u90FD\u662F\u4E00\u65F6\u7684\u5462\uFF1F</p><p>\u770B\u5F97\u591A\u4E86\u6162\u6162\u5C31\u660E\u767D\u4E86\u3002</p><p>\u786E\u5B9E\u662F\u6709\u4E9B\u4F01\u4E1A\u505A\u4E86\u5F88\u591A\u5F88\u591A\u5E74\uFF0C\u4E0D\u8FC7\u90FD\u662F\u4E00\u4E2A\u52A8\u6001\u7684\u8FC7\u7A0B\u3002\u8BFA\u57FA\u4E9A\u8FD9\u4E2A\u516C\u53F8\u5230\u73B0\u5728\u5DF2\u7ECF\u641E\u4E86\u4E00\u767E\u4E94\u5341\u5E74\u4E86\u5427\uFF0C\u4EE5\u524D\u662F\u780D\u6811\u7684\uFF0C\u540E\u6765\u9020\u7EB8\uFF0C\u6A61\u80F6\u5236\u54C1\uFF0C\u7535\u7F06\uFF0C\u5316\u5DE5\u7B49\u7B49\uFF0C\u518D\u540E\u6765\u505A\u624B\u673A\uFF0C\u641E\u901A\u4FE1\uFF0C\u8FD8\u6709\u5176\u4ED6\u526F\u4E1A\uFF0C\u526F\u4E1A\u8DDF\u4E3B\u4E1A\u4E4B\u95F4\u53D8\u6765\u53D8\u53BB\u3002</p><p>\u8FD9\u4E2A\u610F\u4E49\u4E0A\u8BF4\uFF0C\u6BCF\u4E00\u4E2A\u516C\u53F8\u90FD\u662F\u4E00\u8258\u201C\u7279\u4FEE\u65AF\u4E4B\u8239\u201D\uFF0C\u5F00\u51FA\u6E2F\u4E4B\u540E\u6CA1\u51E0\u5929\uFF0C\u4FEE\u4FEE\u8865\u8865\uFF0C\u5C31\u4E0D\u518D\u662F\u4EE5\u524D\u90A3\u8258\u4E86\u3002</p><p>\u817E\u8BAF\u3001\u963F\u91CC\u4ED6\u4EEC\u90A3\u4E9B\u516C\u53F8\u4E5F\u4E00\u6837\uFF0C\u5176\u5B9E\u4E00\u76F4\u90FD\u5728\u53D8\u6765\u53D8\u53BB\u3002\u524D\u6BB5\u65F6\u95F4\u8DDF\u4E00\u4E2A\u963F\u91CC\u505A\u9884\u7814\u7684\u5C0F\u4F19\u4F34\u804A\u4E86\u4E0B\uFF0C\u4ED6\u8BF4<strong>\u5927\u516C\u53F8\u6CA1\u6709\u201C\u805A\u7126\u6838\u5FC3\u4E1A\u52A1\u201D\u7684\uFF0C\u56E0\u4E3A\u4F60\u81EA\u5DF1\u90FD\u4E0D\u77E5\u9053\u4F60\u7684\u6838\u5FC3\u4E1A\u52A1\u80FD\u6301\u7EED\u591A\u4E45\u3002</strong></p><p>\u5927\u5BB6\u90FD\u662F\u4E0D\u65AD\u5730\u5F00\u62D3\u65B0\u6218\u7EBF\uFF0C\u5728\u5404\u4E2A\u65B9\u5411\u4E0A\u7A81\u7834\uFF0C\u54EA\u4E2A\u6709\u8FDB\u5C55\u7B97\u54EA\u4E2A\uFF0C\u6BCF\u5E74\u6D6A\u8D39\u6389\u7684\u94B1\u4E0D\u8BA1\u5176\u6570\uFF0C\u6536\u8D2D\u5C0F\u516C\u53F8\uFF0C\u5F00\u53D1\u65B0\u4EA7\u54C1\uFF0C\u5E76\u4E14\u968F\u65F6\u51C6\u5907\u53D8\u6362\u8D5B\u9053\uFF0C\u6216\u8005\u811A\u8E0F\u597D\u51E0\u6761\u8D5B\u9053\u3002\u5982\u679C\u5929\u5929\u5173\u6CE8\u81EA\u5DF1\u8FD9\u4E00\u4EA9\u4E09\u5206\u5730\uFF0C\u8FDF\u65E9\u88AB\u4E0D\u77E5\u9053\u54EA\u7A9C\u51FA\u6765\u7684\u7ADE\u4E89\u5BF9\u624B\u6253\u4E2A\u63AA\u624B\u4E0D\u53CA\u3002</p><p>\u8FD9\u4E2A\u5C31\u5F88\u6709\u70B9\u50CF\u201C\u8702\u7FA4\u7B97\u6CD5\u201D\u4E86\u3002\u871C\u8702\u4EEC\u667A\u5546\u662F\u8D1F\u7684\uFF0C\u4F46\u662F\u4ED6\u4EEC\u53EF\u4EE5\u901A\u8FC7\u4E00\u4E9B\u7B28\u529E\u6CD5\u6765\u641E\u5B9A\u4E00\u4E9B\u9AD8\u96BE\u5EA6\u64CD\u4F5C\u3002\u6BD4\u5982\u600E\u6837\u627E\u5230\u8FDC\u5904\u7684\u82B1\uFF0C\u4ED6\u4EEC\u5E38\u89C1\u7684\u4E00\u4E2A\u5957\u8DEF\u5C31\u662F\u56DB\u5904\u51FA\u51FB\uFF0C\u54EA\u53EA\u871C\u8702\u627E\u5230\u8D44\u6E90\u7B97\u54EA\u4E2A\uFF0C\u7136\u540E\u5927\u5BB6\u4E00\u62E5\u800C\u4E0A\u3002</p><p>\u5927\u5382\u4E5F\u4E00\u6837\uFF0C\u540C\u65F6\u4E0A\u9A6C\u4E00\u767E\u4E2A\u9879\u76EE\uFF0C\u54EA\u4E2A\u6210\u4E86\u7B97\u54EA\u4E2A\u3002\u53CD\u6B63\u8981\u4E0D\u65AD\u5730\u63A2\u7D22\u8C03\u6574\uFF0C\u6CA1\u5565\u662F\u4E0D\u53D8\u7684\u3002</p><p>\u5982\u679C\u4F60\u4E0D\u53D8\uFF0C\u7FA4\u4F17\u7684\u53E3\u5473\u662F\u5201\u94BB\u7684\uFF0C\u4E00\u65E6\u51FA\u73B0\u7ADE\u54C1\uFF0C\u53EF\u80FD\u77AC\u95F4\u5C31\u629B\u5F03\u4F60\u3002\u6B63\u5982\u5927\u5BB6\u76EE\u7779\u8BFA\u57FA\u4E9A\u8EAB\u4E0A\u53D1\u751F\u7684\u4E8B\u4E00\u6837\u3002</p><p>\u5BF9\u5E94\u5728\u6211\u56FD\u8FD9\u4E9B\u5E74\u53D1\u751F\u7684\u4E8B\uFF0C\u4E5F\u592A\u660E\u663E\u4E86\u3002\u8FD9\u51E0\u5E74\u5927\u4F6C\u4EEC\u7684\u6C89\u6D6E\u8DDF\u8FC7\u6C5F\u7684\u9CA4\u9C7C\u4E00\u6837\uFF0C\u8C01\u8981\u662F\u5904\u5728\u98CE\u53E3\u4E0A\uFF0C\u8C01\u90FD\u7740\u6025\u5F97\u4E00\u6279\uFF0C\u90FD\u60F3\u8D76\u5728\u8FD9\u6CE2\u673A\u9047\u9000\u6F6E\u4E4B\u524D\u628A\u94B1\u90FD\u8D5A\u4E86\uFF0C\u522B\u8BF4\u201C996\u201D\u4E86\uFF0C\u201C007\u201D\u90FD\u4E0D\u662F\u95EE\u9898\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u51E0\u4E4E\u6240\u6709\u8D5A\u5230\u94B1\u7684\u4EBA\u90FD\u6E05\u695A\u4E00\u4E2A\u9053\u7406\uFF0C\u201C\u8D5A\u94B1\u7684\u672C\u8D28\u662F\u4FE1\u606F\u5DEE\u201D\uFF0C\u4E00\u65E6\u4F60\u77E5\u9053\u7684\u4E1C\u897F\u522B\u4EBA\u4E5F\u77E5\u9053\u4E86\uFF0C\u4F60\u5C31\u522B\u60F3\u8D5A\u8FD9\u4E2A\u94B1\u4E86\u3002\u6240\u4EE5\u5927\u5BB6\u90FD\u201C\u4E00\u4E07\u5E74\u592A\u4E45\uFF0C\u53EA\u4E89\u671D\u5915\u201D\uFF0C\u80FD\u4E0D\u6025\u561B\u3002</p><h2 id="_3\u3001\u65F6\u95F4\u611F\u4E0D\u4E00\u6837" tabindex="-1"><a class="header-anchor" href="#_3\u3001\u65F6\u95F4\u611F\u4E0D\u4E00\u6837" aria-hidden="true">#</a> 3\u3001\u65F6\u95F4\u611F\u4E0D\u4E00\u6837</h2><p>**\u5927\u4F6C\u548C\u666E\u901A\u4EBA\u540C\u6837\u662F\u5F00\u5C55\u5DE5\u4F5C\u5EFA\u8BBE\u793E\u4F1A\u4E3B\u4E49\uFF0C\u4F46\u662F\u5FC3\u6001\u5374\u5B8C\u5168\u4E0D\u4E00\u6837\uFF0C\u56E0\u4E3A\u5DE5\u4F5C\u6027\u8D28\u5C31\u4E0D\u4E00\u6837\u3002**\u8BF4\u8FD9\u4E2A\u4E4B\u524D\uFF0C\u54B1\u4EEC\u5148\u8BF4\u4E2A\u522B\u7684\u4E8B\u3002</p><p>\u73B0\u5728\u6709\u4E2A\u65B0\u5174\u7684\u5B66\u79D1\uFF0C\u5927\u5B66\u91CC\u4E0D\u6559\uFF0C\u4F46\u662F\u5728\u6E38\u620F\u4EA7\u4E1A\u754C\u5374\u975E\u5E38\u65F6\u9AE6\uFF0C\u4E13\u95E8\u7814\u7A76\u4EC0\u4E48\u6837\u7684\u673A\u5236\u53EF\u4EE5\u8BA9\u4EBA\u73A9\u6E38\u620F\u5C31\u8DDF\u5438\u6BD2\u90A3\u4E48\u4E0A\u763E\uFF0C\u597D\u50CF\u53EB\u4EC0\u4E48\u201C\u975E\u7269\u8D28\u6210\u763E\u6027\u7814\u7A76\u201D\u3002</p><p>\u8FD9\u73A9\u610F\u5230\u73B0\u5728\u5DF2\u7ECF\u975E\u5E38\u6210\u719F\uFF0C\u6E38\u620F\u5B85\u7537\u4EEC\u90FD\u77E5\u9053\uFF0C\u7C7B\u4F3C\u300A\u6587\u660E6\u300B\u8FD9\u6837\u7684\u6E38\u620F\u672C\u6765\u53EA\u60F3\u518D\u73A9\u4E00\u5C40\u6765\u7740\uFF0C\u4F46\u662F\u8F6C\u773C\u5C31\u662F\u4E00\u4E2A\u901A\u5BB5\u3002\u6211\u8FC7\u5E74\u90A3\u6BB5\u65F6\u95F4\u56E0\u4E3A\u75AB\u60C5\u5728\u5BB6\u4E0A\u73ED\uFF0C\u4E0B\u8F7D\u4E86\u4E24\u4E2A\u6E38\u620F\uFF0C\u300A\u7F3A\u6C27\u300B\u548C\u300A\u8FD9\u662F\u6211\u4EEC\u7684\u6218\u4E89\u300B\uFF0C\u5DEE\u70B9\u73A9\u66B4\u6BD9\u3002\u5C24\u5176\u90A3\u4E2A\u7834\u300A\u7F3A\u6C27\u300B\uFF0C\u751A\u81F3\u62FF\u4E2A\u672C\u5B50\u5728\u90A3\u91CC\u7B97\u6765\u7B97\u53BB\uFF0C\u73A9\u6E38\u620F\u90FD\u73A9\u51FA\u6765\u4E86\u9AD8\u8003\u7684\u4F53\u9A8C\u3002</p><p>\u6700\u5947\u8469\u7684\u662F\u6211\u540C\u4E8B\u3002\u4ED6\u624B\u5E95\u4E0B\u6709\u4E2A\u5C0F\u4F19\u4F34\u665A\u4E0A\u71AC\u591C\u73A9\u300A\u7F3A\u6C27\u300B\u8FDB\u533B\u9662\u4E86\uFF0C\u4ED6\u5F88\u4E0D\u7406\u89E3\uFF0C\u89C9\u5F97\u4ED6\u5C0F\u5F1F\u5927\u8001\u7237\u4EEC\u4E00\u70B9\u90FD\u4E0D\u6210\u719F\uFF0C\u5C31\u4E5F\u53BB\u8BD5\u73A9\u4E86\u4E0B\uFF0C\u7ED3\u679C\u6709\u4E24\u5468\u6BCF\u5929\u665A\u4E0A\u73A9\u5230\u4E24\u4E09\u70B9\uFF0C\u71AC\u591C\u7814\u7A76\u5E03\u6C34\u7BA1\u5B50\u4E0D\u597D\u597D\u5E26\u5C0F\u5B69\u5012\u5783\u573E\u905B\u72D7\uFF0C\u88AB\u4ED6\u5AB3\u5987\u66B4\u6253\uFF0C\u5BB6\u5EAD\u90FD\u5DEE\u70B9\u7834\u88C2\u4E86\u3002</p><p>\u5927\u5BB6\u6709\u5174\u8DA3\u53EF\u4EE5\u53BB\u5C1D\u8BD5\u4E0B\uFF0C\u8FD9\u7C7B\u6E38\u620F\u975E\u5E38\u6311\u4EBA\uFF0C\u53EF\u80FD\u90FD\u73A9\u4E0D\u8FDB\u53BB\uFF0C\u4E00\u65E6\u8FDB\u53BB\u4E86\uFF0C\u8DDF\u9493\u9C7C\u3001\u5438\u6BD2\u6CA1\u5565\u533A\u522B\u3002</p><p>\u4E3A\u5565\u8FD9\u4E9B\u6E38\u620F\u8FD9\u4E48\u5389\u5BB3\u5462\uFF1F</p><p>\u9053\u7406\u4E5F\u4E0D\u590D\u6742\uFF0C\u4ED6\u4EEC\u4ED4\u7EC6\u7814\u7A76\u4E86\u4EBA\u7C7B\u5E95\u5C42\u56FA\u4EF6\u91CC\u7684\u57FA\u672C\u903B\u8F91\uFF0C\u505A\u51FA\u6765\u7684\u4E1C\u897F\u4E13\u95E8\u76F4\u63A5\u523A\u6FC0\u4EBA\u4F53\u5185\u90A3\u4E9B\u6210\u763E\u6309\u94AE\u3002</p><p>\u6BD4\u5982\u4EBA\u7C7B\u672C\u8EAB\u5BF9\u77ED\u671F\u523A\u6FC0\u975E\u5E38\u654F\u611F\uFF0C\u6240\u4EE5\u65E0\u8BBA\u662F\u6E38\u620F\uFF0C\u8FD8\u662F\u7535\u5F71\uFF0C\u751A\u81F3\u7F51\u6587\uFF0C\u90FD\u8981\u201C\u5343\u5B57\u5C0F\u9AD8\u6F6E\u201D\uFF0C\u4E5F\u5C31\u662F\u8BF4\u7F51\u6587\u884C\u4E1A\u91CC\u6BCF\u4E00\u5343\u5B57\uFF0C\u5C31\u5F97\u641E\u4E2A\u523A\u6FC0\u73A9\u610F\u51FA\u6765\uFF0C\u4E0D\u7136\u7EAF\u7CB9\u7684\u5E73\u94FA\u76F4\u53D9\u6CA1\u4EBA\u770B\u3002\u7535\u5F71\u4E5F\u662F\uFF0C\u4E09\u5206\u949F\u4E00\u4E2A\u5C0F\u9AD8\u6F6E\u3002\u6E38\u620F\u57FA\u672C\u4E5F\u9075\u5FAA\u8FD9\u4E2A\u903B\u8F91\uFF0C\u8FD9\u5C31\u662F\u4E3A\u5565\u6709\u201C\u5173\u5361\u201D\u3001\u201C\u5C0Fboss\u201D\u548C\u201C\u5927boss\u201D\u4E4B\u7C7B\u7684\u7EDF\u4E00\u5143\u7D20\u3002</p><p>\u61C2\u4E86\u8FD9\u4E2A\u903B\u8F91\uFF0C\u5927\u5BB6\u5C31\u77E5\u9053\u770B\u7535\u5F71\u5F88\u723D\uFF0C\u7F51\u6587\u5F88\u723D\uFF0C\u6E38\u620F\u5F88\u723D\uFF0C\u6559\u79D1\u4E66\u975E\u5E38\u65E0\u804A\uFF0C\u56E0\u4E3A\u6559\u79D1\u4E66\u6CA1\u6CD5\u505A\u6210\u524D\u8FB9\u90A3\u51E0\u79CD\u597D\u73A9\u4E1C\u4E1C\u3002</p><p>\u6B64\u7C7B\u6E38\u620F\u8FD8\u6709\u201C\u6709\u9650\u6311\u6218\u201D\uFF0C\u201C\u77ED\u671F\u6FC0\u52B1\u201D\uFF0C\u201C\u53CA\u65F6\u53CD\u9988\u201D\u548C\u201C\u968F\u673A\u5956\u52B1\u201D\u7B49\u4E1C\u897F\u3002</p><p>**\u4EBA\u5BF9\u201C\u53CD\u9988\u201D\u6709\u4E25\u91CD\u7684\u4F9D\u8D56\uFF0C\u505A\u4E86\u4E00\u4EF6\u4E8B\uFF0C\u5982\u679C\u8FC5\u901F\u6709\u53CD\u5E94\uFF0C\u5C31\u4F1A\u89E6\u53D1\u5956\u52B1\u673A\u5236\uFF0C\u89C9\u5F97\u975E\u5E38\u8FC7\u763E\u3002**\u6700\u660E\u663E\u7684\u5C31\u662F\u8FD9\u4E24\u5E74\u6BD4\u8F83\u65F6\u9AE6\u7684\u300ALOL\u300B\u548C\u300A\u7EDD\u5730\u6C42\u751F\u300B\uFF0C\u653E\u4E2A\u6280\u80FD\u5C31\u628A\u5BF9\u65B9\u5E72\u6B7B\uFF0C\u6216\u8005\u8FDC\u8FDC\u4E00\u67AA\u7206\u5934\uFF0C\u90FD\u80FD\u4EA7\u751F\u5DE8\u5927\u7684\u6B22\u4E50\u3002\u8FD9\u7C7B\u6E38\u620F\u5F80\u5F80500\u5C0F\u65F6\u8D77\u6B65\uFF0C\u4E5F\u5C31\u662F\u73A9\u5BB6\u4E0D\u77E5\u4E0D\u89C9\u5C31\u80FD\u5728\u6E38\u620F\u91CC\u6CE1500\u4E2A\u5C0F\u65F6\u4EE5\u4E0A\u3002</p><p>\u800C\u4E14\u505A\u7684\u4E8B\u5F97\u7A0D\u5FAE\u6709\u70B9\u6311\u6218\u6027\uFF0C\u5927\u5BB6\u73A9\u6E38\u620F\u4E5F\u6709\u8FD9\u79CD\u611F\u89C9\uFF0C\u5982\u679C\u6E38\u620F\u672C\u8EAB\u6311\u6218\u4E0D\u8DB3\uFF0C\u5F88\u5FEB\u5C31\u4E4F\u5473\u4E86\uFF1B\u5982\u679C\u592A\u6709\u6311\u6218\uFF0C\u7C7B\u4F3C\u300A\u53EA\u72FC\u300B\u90A3\u79CD\uFF0C\u53D7\u4F17\u4E5F\u4E0D\u662F\u592A\u5927\uFF0C\u7EDD\u5927\u90E8\u5206\u4EBA\u90FD\u662F\u4E91\u73A9\u5BB6\uFF0C\u8BF4\u8D77\u6765\u4E00\u5957\u4E00\u5957\u7684\uFF0C\u5176\u5B9E\u4E00\u5200\u90FD\u6CA1\u780D\u8FC7\u3002</p><p>\u73B0\u5728\u56DE\u5230\u4E3B\u9898\uFF0C<strong>\u5982\u679C\u4F60\u5F53\u4E0A\u4E86\u5927\u4F6C\uFF0C\u624B\u5E95\u4E0B\u6709\u4E86\u4EBA\uFF0C\u5C31\u6709\u4E86\u9009\u62E9\u548C\u81EA\u4E3B\u6027\uFF0C\u628A\u4E0D\u613F\u610F\u505A\u4E4F\u5473\u7684\u4E8B\u53EF\u4EE5\u201C\u5916\u5305\u201D\u7ED9\u522B\u4EBA\uFF0C\u4E13\u6CE8\u505A\u70B9\u6709\u6311\u6218\u7684\u4E8B\uFF0C\u5DE5\u4F5C\u7684\u6027\u8D28\u5C31\u53D8\u4E86\uFF0C\u53D8\u5F97\u4E0D\u518D\u50CF\u793E\u755C\uFF0C\u53CD\u5012\u662F\u6709\u70B9\u50CF\u6253\u6E38\u620F\uFF0C\u523A\u6FC0\u800C\u4E0D\u4E4F\u5473\u3002</strong></p><p>\u800C\u4E14\u8D5A\u94B1\u591A\u7684\u65F6\u5019\uFF0C\u6709\u70B9\u50CF\u73A9\u5403\u9E21\uFF0C\u67AA\u67AA\u7206\u5934\uFF0C\u72B6\u6001\u597D\u5F97\u4E0D\u5F97\u4E86\uFF0C\u6709\u826F\u597D\u53CD\u9988\uFF0C\u8D8A\u73A9\u8D8A\u6709\u3002</p><p>\u8FD9\u4E5F\u662F\u4E3A\u5565\u6211\u4E4B\u524D\u8BF4\uFF0C\u521B\u4E1A\u662F\u6761\u5355\u884C\u9053\uFF0C\u4E00\u65E6\u5F00\u59CB\u521B\u4E1A\uFF0C\u57FA\u672C\u5C31\u56DE\u4E0D\u4E86\u5934\u4E86\uFF0C<strong>\u56E0\u4E3A\u90A3\u79CD\u7D27\u5F20\u523A\u6FC0\u3001\u81EA\u5DF1\u505A\u51B3\u5B9A\u81EA\u5DF1\u6536\u94B1\u7684\u6B22\u4E50\u6210\u763E\u6027\u592A\u5F3A\uFF0C\u521B\u4E1A\u8005\u4EEC\u57FA\u672C\u4E0A\u4E0D\u53EF\u80FD\u518D\u56DE\u683C\u5B50\u95F4\uFF0C\u542C\u522B\u4EBA\u6307\u4EE4\u7ED9\u522B\u4EBA\u505A\u4E8B\u4E86\u3002</strong></p><h2 id="_4-\u3001\u6211\u4EEC\u5E94\u8BE5\u505A\u70B9\u5565" tabindex="-1"><a class="header-anchor" href="#_4-\u3001\u6211\u4EEC\u5E94\u8BE5\u505A\u70B9\u5565" aria-hidden="true">#</a> 4 \u3001\u6211\u4EEC\u5E94\u8BE5\u505A\u70B9\u5565</h2><p>\u5176\u5B9E\u8BF4\u4E86\u534A\u5929\uFF0C\u4E0D\u662F\u4E3A\u4E86\u5F00\u4E2A\u5E16\u5B50\u4E13\u95E8\u819C\u62DC\u522B\u4EBA\uFF0C\u800C\u662F\u8BF4\u4E86\u89E3\u4E86\u8FD9\u4E8B\u5BF9\u6211\u4EEC\u81EA\u5DF1\u4E5F\u975E\u5E38\u6709\u88E8\u76CA\u3002</p><p>\u5728\u6211\u770B\u6765\uFF0C\u6709\u4E86\u4E00\u5B9A\u7684\u5BB6\u5E95\u4F9D\u65E7\u594B\u6218\u4E0D\u6B62\u7684\uFF0C\u5F80\u5F80\u662F\u4E00\u79CD\u201C<strong>\u8239\u957F\u601D\u7EF4</strong>\u201D\uFF0C\u5904\u5728\u98CE\u53E3\u4E0A\uFF0C\u4E5F\u610F\u8BC6\u5230\u98CE\u9669\u548C\u673A\u9047\uFF0C\u5E76\u4E14\u53BB\u8FCE\u63A5\u6311\u6218\u3002</p><p>\u4EBA\u6CA1\u6709\u4E0D\u559C\u6B22\u6311\u6218\u7684\uFF0C\u57FA\u56E0\u91CC\u51B3\u5B9A\u7684\uFF0C\u5927\u5BB6\u90FD\u7231\u73A9\u6E38\u620F\uFF0C\u672C\u8D28\u4E5F\u662F\u201C\u6311\u6218\u9002\u5EA6\u201D\u3002\u6709\u4E9B\u4EBA\u89C9\u5F97\u81EA\u5DF1\u662F\u54B8\u9C7C\uFF0C\u5927\u90E8\u5206\u662F\u4E0D\u5C0F\u5FC3\u53C2\u52A0\u4E86\u96BE\u5EA6\u8FC7\u9AD8\u7684\u6E38\u620F\uFF0C\u88AB\u6253\u51FB\u4E86\uFF0C\u6BD4\u5982\u6211\u4E0A\u6B21\u53BB\u73A9\u4E86\u300A\u53EA\u72FC\u300B\u540E\u5C31\u53D1\u8A93\u8FD9\u8F88\u5B50\u90FD\u518D\u4E0D\u73A9\u4E86\u3002</p><p>\u9009\u62E9\u597D\u96BE\u5EA6\uFF0C\u9002\u5F53\u6311\u6218\uFF0C\u4E0D\u65AD\u63D0\u5347\uFF0C\u8C01\u90FD\u80FD\u6709\u4E2A\u597D\u7ED3\u679C\u3002</p><p>\u800C\u4E14\u6211\u4E5F\u7ECF\u5E38\u8BF4\uFF0C\u6210\u5E74\u4EBA\u77E5\u9053\u4E86\u793E\u4F1A\u7684\u771F\u76F8\u540E\uFF0C\u8FD8\u662F\u8981\u65E9\u505A\u51C6\u5907\uFF0C\u5F04\u6E05\u695A\u54EA\u4E9B\u4E8B\u662F\u5728\u4E3A\u8001\u677F\u51D1\u9996\u4ED8\uFF0C\u54EA\u4E9B\u4E8B\u662F\u81EA\u5DF1\u7684\u9996\u4ED8\uFF0C\u907F\u514D\u865A\u5984\u7684\u5B89\u5168\u611F\uFF0C\u5E73\u65F6\u5C31\u5728\u751F\u6D3B\u4E2D\u878D\u5165\u53D8\u5316\uFF0C\u4E0D\u8981\u865A\uFF0C\u6298\u817E\u8D77\u6765\u3002</p><p>\u5173\u952E\u8BCD\uFF1A\u865A\u5984\u7684\u5B89\u5168\u611F \u5E02\u573A\u662F\u4E0D\u65AD\u6CE2\u52A8\u7684 \u53CD\u9988 \u8239\u957F\u601D\u7EF4</p>',62);function _(l,g){const t=c("ExternalLinkIcon");return o(),a("div",null,[p("p",null,[p("em",null,[e("\u539F\u6587\u94FE\u63A5\uFF1A"),p("a",h,[e("https://www.toutiao.com/i6894410680401658379"),n(t)])])]),d])}const m=r(i,[["render",_],["__file","2020-11-13-tou-tiao-i6894410680401658379.html.vue"]]);export{m as default};
